--
-- Table structure for table `tour`
--

CREATE TABLE IF NOT EXISTS `tour` (
  `Ma_tour` int(11) NOT NULL AUTO_INCREMENT,
  `Ten_tour` varchar(128) NOT NULL,
  `Phuong_tien` varchar(64) NOT NULL,
  `Ngay_bat_dau` date NOT NULL,
  `Ngay_ket_thuc` date NOT NULL,
  `Gia` int(16) NOT NULL,
  `Noi_dung` text,
  `Hinh_anh` varchar(256) DEFAULT 'images/default.jpg',
  `Noi_dung2` text,
  `So_cho_toi_da` int(11) NOT NULL DEFAULT '0',
  `AnHien` tinyint(4) NOT NULL DEFAULT '1',
  PRIMARY KEY (`Ma_tour`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=17 ;

--
-- Dumping data for table `tour`
--

INSERT INTO `tour` (`Ma_tour`, `Ten_tour`, `Phuong_tien`, `Ngay_bat_dau`, `Ngay_ket_thuc`, `Gia`, `Noi_dung`, `Hinh_anh`, `Noi_dung2`, `So_cho_toi_da`, `AnHien`) VALUES
(1, 'DU LỊCH CAMPUCHIA 2014 - ƯU ĐÃI ĐẾN BẤT NGỜ', 'Ô tô', '2014-12-29', '2015-01-01', 2890000, 'Campuchia là quốc gia được mệnh danh “đất nước chùa tháp” chứa đựng bao điều bí ẩn làm mê đắm nhiều du khách. Nơi ấy, vẻ đẹp nguyên sơ cùng sự hùng vĩ của Angkor, cung điện Hoàng Gia, Bokor in đậm dấu ấn thời gian. Con người Campuchia dung dị, thân thiện và mến khách sẽ khiến ai cũng phải ngưỡng mộ ngành du lịch Campuchia... ', 'apsara(1).jpg', '\n<h1 style="text-align: center;"><span style="color: red;">Vương Quốc Campuchia</span></h1>\n<div>Campuchia là một quốc gia thuộc Đông Nam Á, nổi tiếng là một quốc gia có nhiều chùa, đền đài và đặc biệt là công trình kiến trúc độc đáo và đầy huyền bí Angkok Wat và Angkok Thom đã thu hút hàng triệu du khách quốc tế hàng năm và hiện tại Campuchia đã trở thành một địa điểm du lịch hấp dẫn nhất thế giới.</div>\n<div><br>\n	</div>\n<div style="text-align: center;">\n	<img src="image_data/tournuocngoai/1/1-1.jpg" alt="vuong-quoc-campuchia" align="" border="0px"></div>\n<h3><span style="color: rgb(107, 142, 35);">Vị trí địa lý Campuchia:</span></h3>\n<div>Vương quốc Campuchia có diện tích khoảng 181.035 km2, phía Bắc giáp với Lào có đường biên giới là 541 km, phía Đông giáp với Việt Nam có đường biên giới là 1.137 km, phía Tây giáp Thái Lan và phía nam giáp với Vịnh Thái Lan có đường biên giới là 800 km.&nbsp;Đồng bằng chiếm 1/2 diện tích tập trung ở hướng Nam và Đông Nam, còn lại là núi, đồi bao quanh đất nước.</div>\n<h3><span style="color: rgb(107, 142, 35);">Lịch sử Campuchia :</span></h3>\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Phù Nam : (Đầu thế kỷ 1 đến năm 550)</span></div>\n<div><br>\n	</div>\n<div>Được biết đến đất nước Campuchia đầu tiên có tên nước là Phù Nam vào khoảng thế kỷ thứ 1, triệu đại Phù Nam đầu tiên qua 4 đời kế vị đó là :&nbsp;Hỗn Điền, Con Hỗn Điền (chưa rõ tên họ), Hỗn Bàn Huống, Hỗn Bàn Bàn, kế tiếp là một vị tướng đã lên ngôi đó là Phạm Sư Man -&gt; Phạm Chiêu -&gt; Phạm Tràng -&gt; Phạm Tầm,..cho đến giữa thế kỷ thứ 7 đất nước Phù Nam chấm dứt do nước Chân Lạp xâm lược.</div>\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);"><br>\n	</span></div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Chân Lạp : ( Năm 550 đến năm 802)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Là một quốc gia do người Khmer sáng lập vào thế kỷ thứ 7 nằm ở Sae Mun (nay thuộc Thái Lan) và Champasack (nay thuộc Nam Lào). Quốc gia này do Bhavavarman sáng lập trong thế kỷ thứ 6, gọi là nước Bhavapura, tức Chân Lạp. Sau khi đánh thắng Phù Nam người Chân Lạp đã ồ ạt di cư xuống phía Nam và dừng chân định cư tại Takeo (cụm di tích Angkor Borey) và Prey Veng (cụm di tích Ba Phnom), trung lưu sông Mekong và Đông Bắc biển Hồ. Isanavarman đã xây dựng kinh đô Isanapura ở gần Kompong Thom.&nbsp;Sau năm 707, Chân Lạp tách thành hai quốc gia Lục Chân Lạp và Thủy Chân Lạp -&gt; Đây chính là thời kỳ khủng hoảng của vương quốc Chân Lạp</div>\n\n\n\n\n\n\n<p><span style="font-weight: bold; color: rgb(0, 0, 205);">Đế quốc Khmer : (Năm 802 đến 1432)</span></p>\n\n\n\n\n\n\n<div>Đầu thế kỷ thứ 9 tức năm 802 vua&nbsp;Jayavarman II đã thống nhất đất nước Campuchia tạo ra một đế chế Khmer hùng mạnh còn gọi là "vương quốc Angkor". Thời kỳ phát triển của đế chế này vào năm&nbsp;944 đến 1181, thời kỳ cực thịnh năm 1181 đến năm 1201, và suy thoái dần từ năm&nbsp;1201 đến năm 1432.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Thời kỳ hậu Angkor : (Năm 1432 đến năm&nbsp;1863)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Từ giữ thế kỷ 15 vương quốc Angkok bị liên tục tấn công bởi&nbsp;Ayutthaya (Thái Lan cổ), đến cuối thế kỷ thứ 17 Angkok chịu sức ép giữ hai nước đang bành trướng đó là Đại Việt và Ayutthaya, đến năm 1863 có người Pháp vào bảo hộ cho vương quốc Angkok, chính vì thề mà vương quốc Angkok đã bị lệ thuộc.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Campuchia thuộc Pháp :&nbsp;(Năm 1863 đến năm 1953)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Năm 1863 Vua Norodom ký một hiệp ước với Pháp để thành lập một chính quyền bảo hộ trên toàn vương quốc.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Campuchia : (Năm 1954 đến năm 1975)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Campuchia tuyên bố độc lập vào năm 1953, trở thành một vương quốc. Năm 1960, Thái tử Norodom Sihanouk lên làm Quốc trưởng chứ không làm vua sau khi vua cha mất. Ông thi hành chính sách trung lập.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Khmer Đỏ cầm quyền: (Năm 1975 đến năm 1979)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Năm 1975&nbsp;thành lập nước "Campuchia Dân chủ" do&nbsp;Khmer Đỏ do Pol Pot lãnh đạo nắm được chính quyền.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Cộng hòa Nhân dân Campuchia (Năm 1979 đến năm 1993)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Ngày 8 tháng 1 năm 1979 Hội đồng Nhân dân Cách mạng nước Cộng hòa Nhân dân Campuchia do Heng Samrin làm Chủ tịch đã được thành lập. Tuy vậy chính quyền này chỉ được một số nước cộng sản công nhận và chưa tự bảo vệ được mà vẫn cần đến sự có mặt của quân đội Việt Nam.</div>\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Campuchia (Tái lập năm 1993 - hiện tại)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n\n\n\n<div>Sau khi cuộc Tổng tuyển cử tự do được Liên Hợp Quốc đứng ra tổ chức tháng 5 năm 1993, các phe phái, chủ yếu các CPP (Đảng Nhân dân Campuchia), FUNCIPEC (Đảng Bảo hoàng)và đảng của Sam Rensi phải mất đến 4 tháng mới thỏa thuận được cơ cấu phân chia quyền lực. Đến tháng 9 năm 1993, Quốc hội và Chính phủ mới được thành lập với nòng cốt là FUNCIPEC và CPP. Quốc hội nhất trí lấy tên nước là Vương quốc Campuchia. Đứng đầu nhà nước là Quốc vương Norodom Sihanuk. Thủ tướng thứ nhất Norodom Ranarit (Chủ tịch Đảng FUNCIPEC), Thủ tướng thứ hai là Hun Xen (Phó Chủ tịch Đảng Nhân dân Campuchia). Chủ tịch quốc hội là Chia Xim (CPP).</div>\n\n\n\n\n<div>Vương quốc Campuchia mở rộng đối ngoại, đối nội, phát triển công nghiệp hóa, nông nghiệp hóa, giao thương với các nước trên thế giới mở rộng tìm nâng cho sự phát triển kinh tế trong tương lai.</div>\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	</div>\n\n\n\n\n<div>Hiện nay vương quốc Campuchia trong thời kỳ phát triển mạnh mẻ đặc biệt là ngành du lịch đa thu hút một lượng khách du lịch quốc tế rất lớn và là một trong những địa điểm du lịch nổi tiếng thế giới.</div>\n\n\n\n\n<div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Thời tiết và khí hậu Campuchia:</span></h3>\n	\n	\n	\n	<div><br>\n		\n		\n		\n		</div>\n	\n	\n	\n	<div>- Thời tiết tại Campuchia có hai mùa rõ rệt, mùa nắng và mùa mưa, mùa mưa kéo dài từ tháng 5 đến tháng 10, mùa mưa kéo dài từ tháng 11 đến tháng 4.</div>\n	\n	\n	\n	<div>- Khí hậu gió mùa nên trở thành vùng nhiệt đới ẩm và khô rõ rệt, nhiệt độ trung bình từ 25 đến 28 độ C, nhiệt độ thấp trung bình là 22 độ C, nhiệt độ cao trung bình là 32 độ C, tuy nhiên có lúc nhiệt độ lên cao đến tận 38 độ C và thấp nhất là 10 độ C nhưng đây là một số năm khí hậu hiếm thấy.</div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Dân tộc và ngôn ngữ Campuchia :&nbsp;</span></h3>\n	\n	\n	\n	<div>Đa phần là dân dộc người Khmer chiếm 90%, dân tộc người Khmer chi thành nhiều loại :&nbsp;Khmer giữa (Khmer Kandal),&nbsp;&nbsp;Khmer Thượng (Khmer Loeur) và Khmer Hạ (Khmer Krom), còn lại là 10% thuộc các dân tộc từ nước khác sang như :&nbsp;Mã Lai, Chàm, Lào, Miến Điện, Việt Nam, Thái Lan, Hoa,.</div>\n	\n	\n	\n	<div>Tiếng Khmer là ngôn ngữ chính (95%), còn lại 5% là được sử dụng tiếng Anh, Pháp Việt, Mã Lai, Trung Hoa,..</div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Du Lịch Campuchia :</span></h3></div>\n\n\n\n\n<div>Sau một thời gian dài 25 năm cô lập, Campuchia mở rộng đón các du khách nước ngoài từ năm 90 và lượng khách tăng dần theo mỗi năm đến thời điểm hiện nay Vương Quốc Campuchia trở thành một địa điểm du lịch hấp dẫn nhất thế giới, những địa điểm thu hút nhất là quần thể Angkok nằm ở Siem Riep, thủ đô&nbsp;Phnom Penh và biển Sihanuk, đấy chính là những địa điểm thu hút khách quốc tế nhất.\n	\n	\n	</div>\n\n<div><br>\n	\n	</div>\n\n<div><span style="font-weight: bold;">Sau đây là những địa điểm du lịch Campuchia :</span></div>\n\n<div><br>\n	\n	</div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/angkor-wat-tai-campuchia.html">Angkor Wat</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/angkor-thom-tai-campuchia.html">Angkor Thom</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/chua-vang-chua-bac-tai-campuchia.html">Chùa vàng - Chùa Bạc</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/tuong-dai-huu-nghi-viet-nam-campuchia.html">Đài tưởng niệm Việt Nam – Cambodia</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;Bãi biển Sihanoukville.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/bao-tang-quoc-gia-tai-campuchia.html">Bảo tàng quốc gia Campuchia</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/nui-ta-lon-tai-campuchia.html">Núi Tà Lơn (Bokor)</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/ngoi-den-banteay-srei-tai-campuchia.html">Ngôi đền Banteay Srei</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/co-do-oudong-tai-campuchia.html">Cố Đô Oudong</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/cung-dien-hoang-gia-tai-campuchia.html">Cung Điện Hoàng Gia</a>.</span></div>\n\n<div><span style="font-weight: bold;">- <a href="http://toptravels.vn/chua-wat-phnom-tai-campuchia.html">Chùa Wat Phnom</a></span></div>\n\n<div>Và một số địa điểm khác nữa.</div>\n\n<div><br>\n	\n	</div>\n\n<div><br>\n	\n	</div>\n', 16, 1),
(2, 'TOUR THÁI LAN GIÁ RẺ 2014 - ƯU ĐÃI ĐẾN BẤT NGỜ', 'Ô tô', '2014-12-30', '2015-01-04', 5850000, 'Thiên đường du lịch Thái Lan nơi con người thân thiện và vui vẻ, đất nước nhiệt đới độc đáo, nền văn hóa và lịch sử lâu đời. Thái Lan tỏa sáng với những ngôi đền rực rỡ nguy nga, những bãi biển vàng và những nụ cười Thái Lan tươi thắm. Du lịch Thái Lan có mọi thứ: cảnh đẹp chùa tháp, bãi biển, thành phố, và ẩm ...', '250814021234.jpg', '\n<div style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: medium; text-align: justify;">\n<h1 style="text-align: center;"><span style="color: red;">DU LỊCH THÁI LAN</span></h1>\n<div style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: medium; text-align: justify;">\n	<ul>\n		<li>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Kỉ lục guiness: Tượng phật Thích Ca dát vàng Cao 118 m, được khắc vào núi Khau Chee Chan dâng tặng đức vua Rama IX nhận dịp nhà vua lên ngôi được 50 năm.</span></span></li>\n		<li>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Thưởng thức các loại Đặc sản Thái Lan trong thực đơn hàng ngày.</span></span></li>\n		<li>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Dạo thuyền tham quan đời sống ven sông của người dân Thái và xem hiện tượng cá nổi trên sông Chao Phra Yao.</span></span></li>\n		<li>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Tìm hiểu ngôi chùa Thuyền - Wat Yannawa – Ngôi chùa có bề dày lịch sử gắn liền với công cuộc đấu tranh giữ nước của dân tộc Thái.</span></span></li>\n		<li>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Đi cano cao tốc tham quan và tắm biển tại đảo Coral (Đảo San Hô).</span></span></li>\n		<li>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Tham quan Toà Nhà Tỷ Phú hoành tráng bậc nhất Thái Lan.</span></span></li>\n		<li>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Vườn Nho Silver Lake</span></span></li>\n		<li>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Thưởng thức chương trình ca nhạc tạp kỹ do các diễn viên chuyển đổi giới tính biểu diễn.</span></span></li>\n		<li>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Tham quan vườn thú hoang dã Safari World Khao Kheow&nbsp;</span></span></li>\n		<li>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Thuơng thức tiệc buffet 5 sao với hàng trăm món ăn đặc sắc ơ Nhà hàng Baiyoke Sky.</span></span></li>\n	</ul>\n	<div>\n		<span><span style="font-family:arial,helvetica,sans-serif;"><strong style="color: rgb(255, 0, 0);">NGÀY 01: TP. HỒ CHÍ MINH – BANGKOK (Ăn nhẹ trên máy bay)</strong></span></span></div>\n</div>\n<div>\n	<span><span style="font-family:arial,helvetica,sans-serif;">Trưởng đoàn của Cty Spacetours sẽ đón quý khách tại sân bay Tân Sơn Nhất,Quốc Tế .Sau đó trưởng đoàn làm thủ tục để đáp chuyến bay đi Thái Lan. Đến sân bay Suvarnabhumi &nbsp;của vương quốc Thái.Sau đó xe đưa Quý khách về khách sạn nhận phòng nghỉ ngơi hoặc tự do khám phá Bangkok về đêm…&nbsp;</span></span></div>\n<div style="text-align: center;">\n	<span><span style="font-family:arial,helvetica,sans-serif;"><img alt="" src="image_data/tournuocngoai/2/2-1.jpg" style="width: 430px; height: 307px;"></span></span></div>\n<div style="text-align: center;">\n	&nbsp;</div>\n<div>\n	<div>\n		<span><span style="font-family:arial,helvetica,sans-serif;"><span style="color:#ff0000;"><strong>NGÀY 02: BANGKOK – PATTAYA (Ăn sáng, trưa, chiều)</strong></span></span></span></div>\n	<div>\n		<div>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Dạo thuyền trên dòng sông Chaophraya Huyền thoại. Xem thuyền Rồng của nhà vua, xem hiện tượng cá nổi trên sông.</span></span></div>\n		<div>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Tham quan Hoàng Cung Ngũ Thế Hoàng, được vua Rama V xây dựng vào năm 1901,và từ đó đến nay vẫn luôn được bảo tồn cẩn thận để quảng bá sự huy hoàng và thịnh vượng của một thời kì hoàng kim.- Đây là tòa nhà bằng gỗ Teak màu vàng đẹp và lớn nhất thế giới,trong Hoàng Cung trưng bày các tác phẩm như: Ngà voi – Thủy Tinh – Đồ vật bằng bạc – Đồ sành sứ - Đồ cổ….&nbsp;</span></span></div>\n		<div>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Sau đó xe đi thẳng về thành phố ma quỷ Pattaya,&nbsp;</span></span></div>\n		<div>\n			&nbsp;</div>\n	</div>\n	<div>\n		&nbsp;</div>\n	<div style="text-align: center;">\n		<span><span style="font-family:arial,helvetica,sans-serif;"><img alt="" src="image_data/tournuocngoai/2/2-2.jpg" style="text-align: center; width: 500px; height: 318px;"></span></span></div>\n	<div>\n		&nbsp;</div>\n	<div>\n		<div>\n			&nbsp;</div>\n		<div>\n			<div>\n				<span><span style="font-family:arial,helvetica,sans-serif;">‘’Đồi vọng cảnh Phra Tamak’’, tại đây Du Khách thỏa sức ngắm cảnh trên biển Pattaya và chiêm ngưỡng vẻ đẹp lộng lẫy của toàn cảnh thành phố nhộn nhịp này. Ngoài ra Du Khách có thể chiêm bái, đốt pháo cầu phúc, cầu may để tỏ lòng tôn kính tại đài tưởng niệm của Đại Tướng Krom Luang Chumporn, cha đẻ của ngành hải quân hoàng gia Thái Lan. Được người dân Thái tôn kính với lòng dũng cảm và ngay thẳng của Ông.</span></span></div>\n			<div>\n				<span><span style="font-family:arial,helvetica,sans-serif;">khi đến nơi Quý khách dùng bữa tối, về khách sạn nhận phòng nghỉ ngơi hoặc tham gia các show về đêm tại Pataya (chi phí tự túc)…</span></span></div>\n			<div>\n				&nbsp;</div>\n		</div>\n	</div>\n	<div style="text-align: center;">\n		<span><span style="font-family:arial,helvetica,sans-serif;"><img alt="" src="image_data/tournuocngoai/2/2-3.jpg" style="width: 500px; height: 303px;"></span></span></div>\n	<div>\n		&nbsp;</div>\n</div>\n<div>\n	<div>\n		<span><span style="font-family:arial,helvetica,sans-serif;"><span style="color:#ff0000;"><strong>NGÀY 03: THAM QUAN PATTAYA - ĐẢO CORAL (Ăn sáng, trưa, chiều)</strong></span></span></span></div>\n	<div>\n		<div>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Sau khi ăn sáng, Quý khách khởi hành đi Đảo San Hô(Coral) Bằng tàu cao tốc, tại đây Quý khách có thể tắm biển hay tham gia các trò chơi trên biển như: Dù kéo, Lướt ván, Lái Canô, Thám hiểm dưới đáy biển…(Chi phí tự túc).</span></span></div>\n		<div>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Tham quan xưởng chế tác đá quý (Một trung tâm trưng bày đá quý đạt tiêu chuẩn ISO 9001) Thái Lan được biết đến là một quốc gia nổi tiếng khai thác về đá quý.</span></span></div>\n		<div>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Tham quan Toà nhà Tỷ Phú - một trong những công trình kiến trúc hoành tráng và tốn kém bậc nhất Thái Lan.</span></span></div>\n		<div>\n			&nbsp;</div>\n		<div>\n			&nbsp;</div>\n	</div>\n	<div style="text-align: center;">\n		<span><span style="font-family:arial,helvetica,sans-serif;"><img alt="" src="image_data/tournuocngoai/2/2-4.jpg" style="width: 432px; height: 268px;"></span></span></div>\n	<div>\n		&nbsp;</div>\n	<div>\n		<div>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Quý khách ăn trưa,Tiếp đến tham quan Trân Bảo Phật Sơn - núi Phật được khắc bằng vàng 24k, cao 140 mét được chiếu bằng tia laze khắc lên vách núi để tặng cho vua Rama IX nhân dịp 50 năm trị vì vương quốc Thái Lan.</span></span></div>\n		<div>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Quý khách tiếp tục tham quan Bảo Tàng Sáp của Thái Lan.</span></span></div>\n		<div>\n			&nbsp;</div>\n		<div style="text-align: center;">\n			<span><span style="font-family:arial,helvetica,sans-serif;"><img alt="" src="image_data/tournuocngoai/2/2-5.jpg" style="text-align: center; width: 500px; height: 305px;"></span></span></div>\n		<div>\n			&nbsp;</div>\n		<div>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Sau đó xe đưa đoàn tham quan Vườn nho Silver Lake, một cánh đồng nho rộng lớn với rất nhiều&nbsp;</span></span></div>\n	</div>\n	<div>\n		<div>\n			<span><span style="font-family:arial,helvetica,sans-serif;">hoa, có cả vườn bầu khổng lồ, bí khổng lồ. Chủ nhân của khu vườn này là một cô gái đẹp, khu vườn này là món quà của một người trong hoàng gia tặng cho người yêu mình. Phía bên ngoài có một cửa hàng lớn bán các sản phẩm làm từ nho, nước ép nho, kẹo nho, mứt nho... Tại đây quý khách có thể tự do mua quà về biếu tặng bạn bè và gia đình.</span></span></div>\n		<div>\n			<span><span style="font-family:arial,helvetica,sans-serif;">Chiều tối. Sau đó quý khách sẽ được tận hưởng dịch vụ massage Thái cổ truyền giúp lưu thông khí huyết và đánh thức sức mạnh tiềm ẩn trong cơ thể. Sau đó sẽ là thời gian tự do mua sắm tại các siêu thị và thưởng thức các món ăn Thái Lan. Quý khách dùng bữa tối và tự do khám phá Pataya…&nbsp;</span></span></div>\n	</div>\n	<div>\n		&nbsp;</div>\n	<div style="text-align: center;">\n		<span><span style="font-family:arial,helvetica,sans-serif;"><img alt="" src="image_data/tournuocngoai/2/2-6.jpg" style="width: 450px; height: 293px;"></span></span></div>\n</div>\n<div>\n	&nbsp;</div>\n<div>\n	<span><span style="font-family:arial,helvetica,sans-serif;"><span style="color:#ff0000;"><strong>NGÀY 04: PATTAYA – BANGKOK (Ăn sáng, trưa, chiều)</strong></span></span></span></div>\n<div>\n	<p>\n		<span><span style="font-family:arial,helvetica,sans-serif;">Ăn sáng tại khách sạn. Xe đưa Quý khách tham quan + Sau khi ăn sáng, Đoàn sẽ tham quan <strong>Vườn thú hoang dã SAFARI WORLD KHAO KHEOW </strong>&nbsp;- Tại đây quý khách sẽ được tận mắt chứng kiến đời sống hoang dã của những loài thú quý hiếm trên Thế Giới, tận hưởng các chương trình show biểu diễn hoành tráng</span></span></p>\n	<p>\n		<span><span style="font-family:arial,helvetica,sans-serif;"><strong>Tham quan Trung tâm yến sào </strong>và tìm hiểu về các loại yến.</span></span></p>\n	<p>\n		<span><span style="font-family:arial,helvetica,sans-serif;">Đến <strong>cửa hàng bán các loại thổ sản Thái </strong>để dùng thử và mua sắm.</span></span></p>\n</div>\n<div style="text-align: center;">\n	<span><span style="font-family:arial,helvetica,sans-serif;"><img alt="" src="image_data/tournuocngoai/2/2-7.jpg" style="width: 500px; height: 336px;"></span></span></div>\n<div>\n	&nbsp;</div>\n<div>\n	<p>\n		<span><span style="font-family:arial,helvetica,sans-serif;"><strong><em>Quý khách được thưởng thức BUFFET cao cấp với hơn 150 món tự chọn: Tổ Yến, Thịt Bò ngoại nhập…tại Tòa Nhà 88 tầng Baiyoke Skye</em></strong>&nbsp;– cao nhất thủ đô Bangkok. Tại đây quý khách còn đươc chiêm ngưỡng toàn cảnh thủ đô Bangkok và lưu lại những khoảnh khắc tuyện vời tại đây.</span></span></p>\n	<p>\n		<span><span style="font-family:arial,helvetica,sans-serif;">Tham quan&nbsp;<strong>chùa Phật Vàng lớn nhất thế giới</strong>: cao 3 mét và nặng hơn 5,5 tấn. Tượng được đúc theo phong cách Sukhothai tĩnh lặng và được khám phá một cách tình cờ vào thập niên 1950 Người địa phương cho rằng bức tượng lớn nhất thế giới này biểu thị cho sự thịnh vượng và thuần khiết cũng như sức mạnh và quyền năng.</span></span></p>\n</div>\n<div>\n	&nbsp;</div>\n<div style="text-align: center;">\n	<span><span style="font-family:arial,helvetica,sans-serif;"><img alt="" src="image_data/tournuocngoai/2/2-8.jpg" style="width: 500px; height: 335px;"></span></span></div>\n<div>\n	&nbsp;</div>\n<div>\n	<p>\n		<span><span style="font-family:arial,helvetica,sans-serif;">Tham quan&nbsp;<strong>Trung tâm nghiên cứu rắn độc Hoàng gia.</strong></span></span></p>\n	<p>\n		<span><span style="font-family:arial,helvetica,sans-serif;">Đoàn đến tham quan&nbsp;<strong>ngôi chùa thuyền Wat Yannawa</strong>&nbsp;nổi tiếng linh thiêng nhất Bangkok</span></span></p>\n	<p>\n		<span><span style="font-family:arial,helvetica,sans-serif;">Sau đó Quý khách sẽ thưởng thức&nbsp;<strong>chương trình biểu diễn ca múa nhạc hoành tráng (show Bêđê) do các vũ công chuyển đổi giới tính thực hiện</strong></span></span></p>\n	<p>\n		&nbsp;</p>\n</div>\n<div>\n	<span><span style="font-family:arial,helvetica,sans-serif;"><span style="color:#ff0000;"><strong>NGÀY 05: THAM QUAN BANGKOK (free day)</strong></span></span></span></div>\n<div>\n	<p>\n		<span><span style="font-family:arial,helvetica,sans-serif;">Ăn sáng tại khách sạn. Quý khách tự do đi tham quan mua sắm tại các siêu thị lớn như: WORLD TRADE CENTRE, BAIYOKE SKY, PRATUNAM MARKET, PLATINUM, ROBINSON SILOM, RACHADA SAGO MARKET</span></span></p>\n	<p>\n		<span><span style="font-family:arial,helvetica,sans-serif;">Quý khách có thể đăng ký tham quan các địa danh nổi tiếng khác của Thái Lan như:&nbsp;<strong>Đền cổ Ayutthaya, Mini Siam, Siam Miramit,….( chí phí tự túc )</strong></span></span></p>\n</div>\n<div style="text-align: center;">\n	<span><span style="font-family:arial,helvetica,sans-serif;"><img alt="" src="image_data/tournuocngoai/2/2-9.jpg" style="width: 500px; height: 333px;"></span></span></div>\n<div>\n	&nbsp;</div>\n<div>\n	<span><span style="font-family:arial,helvetica,sans-serif;"><span style="color:#ff0000;"><strong>NGÀY 06: &nbsp;BANGKOK - TP. HỒ CHÍ MINH (Ăn sáng)</strong></span></span></span></div>\n<div>\n	<span><span style="font-family:arial,helvetica,sans-serif;">Ăn sáng tại khách sạn. Đoàn khởi hành tham quan Trung Tâm trưng bày các loại thuộc da nổi tiếng của Thái Lan như da cá sấu, cá đuối, voi ... Sau đó xe đưa Quý khách ra sân bay Survanahumi đáp chuyến bay về TP.HCM. Đoàn về đến sân bay Tân Sơn Nhất. Kết thúc chuyến tham quan. Chia tay đoàn và hẹn gặp lại.</span></span></div>\n<div>\n	&nbsp;</div>\n<div>\n	&nbsp;</div>\n\n                                                </div>\n', 32, 1),
(3, 'Khám phá Bảo Lộc 2014', 'Ô tô', '2014-12-30', '2015-01-01', 1675000, 'Trong khí hậu se lạnh ở miền cao, bên bờ hồ thơ mộng khu du lịch DamB''ri, Quý khách thưởng thức hương vị cà phê B''lao hoặc sinh hoạt lửa trại, giao lưu văn nghệ cồng chiêng với đồng bào dân tộc Châu Mạ, uống rượu cần, ăn cơm lam, thịt nướng…nghỉ đêm tại Bảo Lộc. Một chuyến đi vô cùng hấp dẫn...', '180713093249.jpg', '\n<h1 style="text-align: center;"><span style="color: red;">KHÁM PHÁ BẢO LỘC</span></h1>\n<div>Campuchia là một quốc gia thuộc Đông Nam Á, nổi tiếng là một quốc gia có nhiều chùa, đền đài và đặc biệt là công trình kiến trúc độc đáo và đầy huyền bí Angkok Wat và Angkok Thom đã thu hút hàng triệu du khách quốc tế hàng năm và hiện tại Campuchia đã trở thành một địa điểm du lịch hấp dẫn nhất thế giới.</div>\n<div><br>\n	</div>\n<div style="text-align: center;">\n	<img src="image_data/tournuocngoai/1/1-1.jpg" alt="vuong-quoc-campuchia" align="" border="0px"></div>\n<h3><span style="color: rgb(107, 142, 35);">Vị trí địa lý Campuchia:</span></h3>\n<div>Vương quốc Campuchia có diện tích khoảng 181.035 km2, phía Bắc giáp với Lào có đường biên giới là 541 km, phía Đông giáp với Việt Nam có đường biên giới là 1.137 km, phía Tây giáp Thái Lan và phía nam giáp với Vịnh Thái Lan có đường biên giới là 800 km.&nbsp;Đồng bằng chiếm 1/2 diện tích tập trung ở hướng Nam và Đông Nam, còn lại là núi, đồi bao quanh đất nước.</div>\n<h3><span style="color: rgb(107, 142, 35);">Lịch sử Campuchia :</span></h3>\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Phù Nam : (Đầu thế kỷ 1 đến năm 550)</span></div>\n<div><br>\n	</div>\n<div>Được biết đến đất nước Campuchia đầu tiên có tên nước là Phù Nam vào khoảng thế kỷ thứ 1, triệu đại Phù Nam đầu tiên qua 4 đời kế vị đó là :&nbsp;Hỗn Điền, Con Hỗn Điền (chưa rõ tên họ), Hỗn Bàn Huống, Hỗn Bàn Bàn, kế tiếp là một vị tướng đã lên ngôi đó là Phạm Sư Man -&gt; Phạm Chiêu -&gt; Phạm Tràng -&gt; Phạm Tầm,..cho đến giữa thế kỷ thứ 7 đất nước Phù Nam chấm dứt do nước Chân Lạp xâm lược.</div>\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);"><br>\n	</span></div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Chân Lạp : ( Năm 550 đến năm 802)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Là một quốc gia do người Khmer sáng lập vào thế kỷ thứ 7 nằm ở Sae Mun (nay thuộc Thái Lan) và Champasack (nay thuộc Nam Lào). Quốc gia này do Bhavavarman sáng lập trong thế kỷ thứ 6, gọi là nước Bhavapura, tức Chân Lạp. Sau khi đánh thắng Phù Nam người Chân Lạp đã ồ ạt di cư xuống phía Nam và dừng chân định cư tại Takeo (cụm di tích Angkor Borey) và Prey Veng (cụm di tích Ba Phnom), trung lưu sông Mekong và Đông Bắc biển Hồ. Isanavarman đã xây dựng kinh đô Isanapura ở gần Kompong Thom.&nbsp;Sau năm 707, Chân Lạp tách thành hai quốc gia Lục Chân Lạp và Thủy Chân Lạp -&gt; Đây chính là thời kỳ khủng hoảng của vương quốc Chân Lạp</div>\n\n\n\n\n\n\n<p><span style="font-weight: bold; color: rgb(0, 0, 205);">Đế quốc Khmer : (Năm 802 đến 1432)</span></p>\n\n\n\n\n\n\n<div>Đầu thế kỷ thứ 9 tức năm 802 vua&nbsp;Jayavarman II đã thống nhất đất nước Campuchia tạo ra một đế chế Khmer hùng mạnh còn gọi là "vương quốc Angkor". Thời kỳ phát triển của đế chế này vào năm&nbsp;944 đến 1181, thời kỳ cực thịnh năm 1181 đến năm 1201, và suy thoái dần từ năm&nbsp;1201 đến năm 1432.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Thời kỳ hậu Angkor : (Năm 1432 đến năm&nbsp;1863)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Từ giữ thế kỷ 15 vương quốc Angkok bị liên tục tấn công bởi&nbsp;Ayutthaya (Thái Lan cổ), đến cuối thế kỷ thứ 17 Angkok chịu sức ép giữ hai nước đang bành trướng đó là Đại Việt và Ayutthaya, đến năm 1863 có người Pháp vào bảo hộ cho vương quốc Angkok, chính vì thề mà vương quốc Angkok đã bị lệ thuộc.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Campuchia thuộc Pháp :&nbsp;(Năm 1863 đến năm 1953)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Năm 1863 Vua Norodom ký một hiệp ước với Pháp để thành lập một chính quyền bảo hộ trên toàn vương quốc.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Campuchia : (Năm 1954 đến năm 1975)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Campuchia tuyên bố độc lập vào năm 1953, trở thành một vương quốc. Năm 1960, Thái tử Norodom Sihanouk lên làm Quốc trưởng chứ không làm vua sau khi vua cha mất. Ông thi hành chính sách trung lập.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Khmer Đỏ cầm quyền: (Năm 1975 đến năm 1979)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Năm 1975&nbsp;thành lập nước "Campuchia Dân chủ" do&nbsp;Khmer Đỏ do Pol Pot lãnh đạo nắm được chính quyền.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Cộng hòa Nhân dân Campuchia (Năm 1979 đến năm 1993)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Ngày 8 tháng 1 năm 1979 Hội đồng Nhân dân Cách mạng nước Cộng hòa Nhân dân Campuchia do Heng Samrin làm Chủ tịch đã được thành lập. Tuy vậy chính quyền này chỉ được một số nước cộng sản công nhận và chưa tự bảo vệ được mà vẫn cần đến sự có mặt của quân đội Việt Nam.</div>\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Campuchia (Tái lập năm 1993 - hiện tại)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n\n\n\n<div>Sau khi cuộc Tổng tuyển cử tự do được Liên Hợp Quốc đứng ra tổ chức tháng 5 năm 1993, các phe phái, chủ yếu các CPP (Đảng Nhân dân Campuchia), FUNCIPEC (Đảng Bảo hoàng)và đảng của Sam Rensi phải mất đến 4 tháng mới thỏa thuận được cơ cấu phân chia quyền lực. Đến tháng 9 năm 1993, Quốc hội và Chính phủ mới được thành lập với nòng cốt là FUNCIPEC và CPP. Quốc hội nhất trí lấy tên nước là Vương quốc Campuchia. Đứng đầu nhà nước là Quốc vương Norodom Sihanuk. Thủ tướng thứ nhất Norodom Ranarit (Chủ tịch Đảng FUNCIPEC), Thủ tướng thứ hai là Hun Xen (Phó Chủ tịch Đảng Nhân dân Campuchia). Chủ tịch quốc hội là Chia Xim (CPP).</div>\n\n\n\n\n<div>Vương quốc Campuchia mở rộng đối ngoại, đối nội, phát triển công nghiệp hóa, nông nghiệp hóa, giao thương với các nước trên thế giới mở rộng tìm nâng cho sự phát triển kinh tế trong tương lai.</div>\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	</div>\n\n\n\n\n<div>Hiện nay vương quốc Campuchia trong thời kỳ phát triển mạnh mẻ đặc biệt là ngành du lịch đa thu hút một lượng khách du lịch quốc tế rất lớn và là một trong những địa điểm du lịch nổi tiếng thế giới.</div>\n\n\n\n\n<div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Thời tiết và khí hậu Campuchia:</span></h3>\n	\n	\n	\n	<div><br>\n		\n		\n		\n		</div>\n	\n	\n	\n	<div>- Thời tiết tại Campuchia có hai mùa rõ rệt, mùa nắng và mùa mưa, mùa mưa kéo dài từ tháng 5 đến tháng 10, mùa mưa kéo dài từ tháng 11 đến tháng 4.</div>\n	\n	\n	\n	<div>- Khí hậu gió mùa nên trở thành vùng nhiệt đới ẩm và khô rõ rệt, nhiệt độ trung bình từ 25 đến 28 độ C, nhiệt độ thấp trung bình là 22 độ C, nhiệt độ cao trung bình là 32 độ C, tuy nhiên có lúc nhiệt độ lên cao đến tận 38 độ C và thấp nhất là 10 độ C nhưng đây là một số năm khí hậu hiếm thấy.</div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Dân tộc và ngôn ngữ Campuchia :&nbsp;</span></h3>\n	\n	\n	\n	<div>Đa phần là dân dộc người Khmer chiếm 90%, dân tộc người Khmer chi thành nhiều loại :&nbsp;Khmer giữa (Khmer Kandal),&nbsp;&nbsp;Khmer Thượng (Khmer Loeur) và Khmer Hạ (Khmer Krom), còn lại là 10% thuộc các dân tộc từ nước khác sang như :&nbsp;Mã Lai, Chàm, Lào, Miến Điện, Việt Nam, Thái Lan, Hoa,.</div>\n	\n	\n	\n	<div>Tiếng Khmer là ngôn ngữ chính (95%), còn lại 5% là được sử dụng tiếng Anh, Pháp Việt, Mã Lai, Trung Hoa,..</div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Du Lịch Campuchia :</span></h3></div>\n\n\n\n\n<div>Sau một thời gian dài 25 năm cô lập, Campuchia mở rộng đón các du khách nước ngoài từ năm 90 và lượng khách tăng dần theo mỗi năm đến thời điểm hiện nay Vương Quốc Campuchia trở thành một địa điểm du lịch hấp dẫn nhất thế giới, những địa điểm thu hút nhất là quần thể Angkok nằm ở Siem Riep, thủ đô&nbsp;Phnom Penh và biển Sihanuk, đấy chính là những địa điểm thu hút khách quốc tế nhất.\n	\n	\n	</div>\n\n<div><br>\n	\n	</div>\n\n<div><span style="font-weight: bold;">Sau đây là những địa điểm du lịch Campuchia :</span></div>\n\n<div><br>\n	\n	</div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/angkor-wat-tai-campuchia.html">Angkor Wat</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/angkor-thom-tai-campuchia.html">Angkor Thom</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/chua-vang-chua-bac-tai-campuchia.html">Chùa vàng - Chùa Bạc</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/tuong-dai-huu-nghi-viet-nam-campuchia.html">Đài tưởng niệm Việt Nam – Cambodia</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;Bãi biển Sihanoukville.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/bao-tang-quoc-gia-tai-campuchia.html">Bảo tàng quốc gia Campuchia</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/nui-ta-lon-tai-campuchia.html">Núi Tà Lơn (Bokor)</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/ngoi-den-banteay-srei-tai-campuchia.html">Ngôi đền Banteay Srei</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/co-do-oudong-tai-campuchia.html">Cố Đô Oudong</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/cung-dien-hoang-gia-tai-campuchia.html">Cung Điện Hoàng Gia</a>.</span></div>\n\n<div><span style="font-weight: bold;">- <a href="http://toptravels.vn/chua-wat-phnom-tai-campuchia.html">Chùa Wat Phnom</a></span></div>\n\n<div>Và một số địa điểm khác nữa.</div>\n\n<div><br>\n	\n	</div>\n\n<div><br>\n	\n	</div>\n', 64, 1);
INSERT INTO `tour` (`Ma_tour`, `Ten_tour`, `Phuong_tien`, `Ngay_bat_dau`, `Ngay_ket_thuc`, `Gia`, `Noi_dung`, `Hinh_anh`, `Noi_dung2`, `So_cho_toi_da`, `AnHien`) VALUES
(5, 'Du lịch Tiền Giang - Bến Tre 2014', 'Ô tô', '2014-12-29', '2014-12-30', 560000, 'Đón khách tại điểm hẹn và khởi hành theo quốc lộ 1A, tới Mỹ Tho, du khách ăn sáng, xe đưa đoàn đi qua vài khu phố thị, du khách tham quan một thoáng thành phố đã có thời rất phồn vinh với tên "Mỹ Tho Đại Phố". Tới bến tàu du lịch Chương Dương, du khách ngồi tàu vượt sông Tiền, thưởng ngoạn phong cảnh xanh tươi của cây trái, trên các cù lao...', '190713092425.jpg', '\n<h1 style="text-align: center;"><span style="color: red;">DU LỊCH TIỀN GIANG</span></h1>\n<div>Campuchia là một quốc gia thuộc Đông Nam Á, nổi tiếng là một quốc gia có nhiều chùa, đền đài và đặc biệt là công trình kiến trúc độc đáo và đầy huyền bí Angkok Wat và Angkok Thom đã thu hút hàng triệu du khách quốc tế hàng năm và hiện tại Campuchia đã trở thành một địa điểm du lịch hấp dẫn nhất thế giới.</div>\n<div><br>\n	</div>\n<div style="text-align: center;">\n	<img src="image_data/tournuocngoai/1/1-1.jpg" alt="vuong-quoc-campuchia" align="" border="0px"></div>\n<h3><span style="color: rgb(107, 142, 35);">Vị trí địa lý Campuchia:</span></h3>\n<div>Vương quốc Campuchia có diện tích khoảng 181.035 km2, phía Bắc giáp với Lào có đường biên giới là 541 km, phía Đông giáp với Việt Nam có đường biên giới là 1.137 km, phía Tây giáp Thái Lan và phía nam giáp với Vịnh Thái Lan có đường biên giới là 800 km.&nbsp;Đồng bằng chiếm 1/2 diện tích tập trung ở hướng Nam và Đông Nam, còn lại là núi, đồi bao quanh đất nước.</div>\n<h3><span style="color: rgb(107, 142, 35);">Lịch sử Campuchia :</span></h3>\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Phù Nam : (Đầu thế kỷ 1 đến năm 550)</span></div>\n<div><br>\n	</div>\n<div>Được biết đến đất nước Campuchia đầu tiên có tên nước là Phù Nam vào khoảng thế kỷ thứ 1, triệu đại Phù Nam đầu tiên qua 4 đời kế vị đó là :&nbsp;Hỗn Điền, Con Hỗn Điền (chưa rõ tên họ), Hỗn Bàn Huống, Hỗn Bàn Bàn, kế tiếp là một vị tướng đã lên ngôi đó là Phạm Sư Man -&gt; Phạm Chiêu -&gt; Phạm Tràng -&gt; Phạm Tầm,..cho đến giữa thế kỷ thứ 7 đất nước Phù Nam chấm dứt do nước Chân Lạp xâm lược.</div>\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);"><br>\n	</span></div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Chân Lạp : ( Năm 550 đến năm 802)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Là một quốc gia do người Khmer sáng lập vào thế kỷ thứ 7 nằm ở Sae Mun (nay thuộc Thái Lan) và Champasack (nay thuộc Nam Lào). Quốc gia này do Bhavavarman sáng lập trong thế kỷ thứ 6, gọi là nước Bhavapura, tức Chân Lạp. Sau khi đánh thắng Phù Nam người Chân Lạp đã ồ ạt di cư xuống phía Nam và dừng chân định cư tại Takeo (cụm di tích Angkor Borey) và Prey Veng (cụm di tích Ba Phnom), trung lưu sông Mekong và Đông Bắc biển Hồ. Isanavarman đã xây dựng kinh đô Isanapura ở gần Kompong Thom.&nbsp;Sau năm 707, Chân Lạp tách thành hai quốc gia Lục Chân Lạp và Thủy Chân Lạp -&gt; Đây chính là thời kỳ khủng hoảng của vương quốc Chân Lạp</div>\n\n\n\n\n\n\n<p><span style="font-weight: bold; color: rgb(0, 0, 205);">Đế quốc Khmer : (Năm 802 đến 1432)</span></p>\n\n\n\n\n\n\n<div>Đầu thế kỷ thứ 9 tức năm 802 vua&nbsp;Jayavarman II đã thống nhất đất nước Campuchia tạo ra một đế chế Khmer hùng mạnh còn gọi là "vương quốc Angkor". Thời kỳ phát triển của đế chế này vào năm&nbsp;944 đến 1181, thời kỳ cực thịnh năm 1181 đến năm 1201, và suy thoái dần từ năm&nbsp;1201 đến năm 1432.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Thời kỳ hậu Angkor : (Năm 1432 đến năm&nbsp;1863)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Từ giữ thế kỷ 15 vương quốc Angkok bị liên tục tấn công bởi&nbsp;Ayutthaya (Thái Lan cổ), đến cuối thế kỷ thứ 17 Angkok chịu sức ép giữ hai nước đang bành trướng đó là Đại Việt và Ayutthaya, đến năm 1863 có người Pháp vào bảo hộ cho vương quốc Angkok, chính vì thề mà vương quốc Angkok đã bị lệ thuộc.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Campuchia thuộc Pháp :&nbsp;(Năm 1863 đến năm 1953)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Năm 1863 Vua Norodom ký một hiệp ước với Pháp để thành lập một chính quyền bảo hộ trên toàn vương quốc.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Campuchia : (Năm 1954 đến năm 1975)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Campuchia tuyên bố độc lập vào năm 1953, trở thành một vương quốc. Năm 1960, Thái tử Norodom Sihanouk lên làm Quốc trưởng chứ không làm vua sau khi vua cha mất. Ông thi hành chính sách trung lập.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Khmer Đỏ cầm quyền: (Năm 1975 đến năm 1979)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Năm 1975&nbsp;thành lập nước "Campuchia Dân chủ" do&nbsp;Khmer Đỏ do Pol Pot lãnh đạo nắm được chính quyền.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Cộng hòa Nhân dân Campuchia (Năm 1979 đến năm 1993)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Ngày 8 tháng 1 năm 1979 Hội đồng Nhân dân Cách mạng nước Cộng hòa Nhân dân Campuchia do Heng Samrin làm Chủ tịch đã được thành lập. Tuy vậy chính quyền này chỉ được một số nước cộng sản công nhận và chưa tự bảo vệ được mà vẫn cần đến sự có mặt của quân đội Việt Nam.</div>\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Campuchia (Tái lập năm 1993 - hiện tại)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n\n\n\n<div>Sau khi cuộc Tổng tuyển cử tự do được Liên Hợp Quốc đứng ra tổ chức tháng 5 năm 1993, các phe phái, chủ yếu các CPP (Đảng Nhân dân Campuchia), FUNCIPEC (Đảng Bảo hoàng)và đảng của Sam Rensi phải mất đến 4 tháng mới thỏa thuận được cơ cấu phân chia quyền lực. Đến tháng 9 năm 1993, Quốc hội và Chính phủ mới được thành lập với nòng cốt là FUNCIPEC và CPP. Quốc hội nhất trí lấy tên nước là Vương quốc Campuchia. Đứng đầu nhà nước là Quốc vương Norodom Sihanuk. Thủ tướng thứ nhất Norodom Ranarit (Chủ tịch Đảng FUNCIPEC), Thủ tướng thứ hai là Hun Xen (Phó Chủ tịch Đảng Nhân dân Campuchia). Chủ tịch quốc hội là Chia Xim (CPP).</div>\n\n\n\n\n<div>Vương quốc Campuchia mở rộng đối ngoại, đối nội, phát triển công nghiệp hóa, nông nghiệp hóa, giao thương với các nước trên thế giới mở rộng tìm nâng cho sự phát triển kinh tế trong tương lai.</div>\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	</div>\n\n\n\n\n<div>Hiện nay vương quốc Campuchia trong thời kỳ phát triển mạnh mẻ đặc biệt là ngành du lịch đa thu hút một lượng khách du lịch quốc tế rất lớn và là một trong những địa điểm du lịch nổi tiếng thế giới.</div>\n\n\n\n\n<div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Thời tiết và khí hậu Campuchia:</span></h3>\n	\n	\n	\n	<div><br>\n		\n		\n		\n		</div>\n	\n	\n	\n	<div>- Thời tiết tại Campuchia có hai mùa rõ rệt, mùa nắng và mùa mưa, mùa mưa kéo dài từ tháng 5 đến tháng 10, mùa mưa kéo dài từ tháng 11 đến tháng 4.</div>\n	\n	\n	\n	<div>- Khí hậu gió mùa nên trở thành vùng nhiệt đới ẩm và khô rõ rệt, nhiệt độ trung bình từ 25 đến 28 độ C, nhiệt độ thấp trung bình là 22 độ C, nhiệt độ cao trung bình là 32 độ C, tuy nhiên có lúc nhiệt độ lên cao đến tận 38 độ C và thấp nhất là 10 độ C nhưng đây là một số năm khí hậu hiếm thấy.</div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Dân tộc và ngôn ngữ Campuchia :&nbsp;</span></h3>\n	\n	\n	\n	<div>Đa phần là dân dộc người Khmer chiếm 90%, dân tộc người Khmer chi thành nhiều loại :&nbsp;Khmer giữa (Khmer Kandal),&nbsp;&nbsp;Khmer Thượng (Khmer Loeur) và Khmer Hạ (Khmer Krom), còn lại là 10% thuộc các dân tộc từ nước khác sang như :&nbsp;Mã Lai, Chàm, Lào, Miến Điện, Việt Nam, Thái Lan, Hoa,.</div>\n	\n	\n	\n	<div>Tiếng Khmer là ngôn ngữ chính (95%), còn lại 5% là được sử dụng tiếng Anh, Pháp Việt, Mã Lai, Trung Hoa,..</div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Du Lịch Campuchia :</span></h3></div>\n\n\n\n\n<div>Sau một thời gian dài 25 năm cô lập, Campuchia mở rộng đón các du khách nước ngoài từ năm 90 và lượng khách tăng dần theo mỗi năm đến thời điểm hiện nay Vương Quốc Campuchia trở thành một địa điểm du lịch hấp dẫn nhất thế giới, những địa điểm thu hút nhất là quần thể Angkok nằm ở Siem Riep, thủ đô&nbsp;Phnom Penh và biển Sihanuk, đấy chính là những địa điểm thu hút khách quốc tế nhất.\n	\n	\n	</div>\n\n<div><br>\n	\n	</div>\n\n<div><span style="font-weight: bold;">Sau đây là những địa điểm du lịch Campuchia :</span></div>\n\n<div><br>\n	\n	</div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/angkor-wat-tai-campuchia.html">Angkor Wat</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/angkor-thom-tai-campuchia.html">Angkor Thom</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/chua-vang-chua-bac-tai-campuchia.html">Chùa vàng - Chùa Bạc</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/tuong-dai-huu-nghi-viet-nam-campuchia.html">Đài tưởng niệm Việt Nam – Cambodia</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;Bãi biển Sihanoukville.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/bao-tang-quoc-gia-tai-campuchia.html">Bảo tàng quốc gia Campuchia</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/nui-ta-lon-tai-campuchia.html">Núi Tà Lơn (Bokor)</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/ngoi-den-banteay-srei-tai-campuchia.html">Ngôi đền Banteay Srei</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/co-do-oudong-tai-campuchia.html">Cố Đô Oudong</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/cung-dien-hoang-gia-tai-campuchia.html">Cung Điện Hoàng Gia</a>.</span></div>\n\n<div><span style="font-weight: bold;">- <a href="http://toptravels.vn/chua-wat-phnom-tai-campuchia.html">Chùa Wat Phnom</a></span></div>\n\n<div>Và một số địa điểm khác nữa.</div>\n\n<div><br>\n	\n	</div>\n\n<div><br>\n	\n	</div>\n', 32, 1),
(6, 'Du lịch Tràm Chim - Gáo Giồng 2014', 'Ô tô', '2014-12-30', '2014-12-31', 1580000, 'Quý khách dùng buổi chiều tại thị trấn Tràm Chim với các món ăn dân dã đặc trưng theo mùa của vùng Đồng Tháp Mười như: lẩu cháo lươn, cá trê nướng mắm gừng, canh chua cá lócđồng, điên điển xào tép...\r\nĐoàn về lại thành phố Cao Lãnh nhận phòng nghỉ ngơi. Nghỉ đêm tại Cao Lãnh.', '190713090205.jpg', '\n<h1 style="text-align: center;"><span style="color: red;">DU LỊCH TRÀM CHIM</span></h1>\n<div>Campuchia là một quốc gia thuộc Đông Nam Á, nổi tiếng là một quốc gia có nhiều chùa, đền đài và đặc biệt là công trình kiến trúc độc đáo và đầy huyền bí Angkok Wat và Angkok Thom đã thu hút hàng triệu du khách quốc tế hàng năm và hiện tại Campuchia đã trở thành một địa điểm du lịch hấp dẫn nhất thế giới.</div>\n<div><br>\n	</div>\n<div style="text-align: center;">\n	<img src="image_data/tournuocngoai/1/1-1.jpg" alt="vuong-quoc-campuchia" align="" border="0px"></div>\n<h3><span style="color: rgb(107, 142, 35);">Vị trí địa lý Campuchia:</span></h3>\n<div>Vương quốc Campuchia có diện tích khoảng 181.035 km2, phía Bắc giáp với Lào có đường biên giới là 541 km, phía Đông giáp với Việt Nam có đường biên giới là 1.137 km, phía Tây giáp Thái Lan và phía nam giáp với Vịnh Thái Lan có đường biên giới là 800 km.&nbsp;Đồng bằng chiếm 1/2 diện tích tập trung ở hướng Nam và Đông Nam, còn lại là núi, đồi bao quanh đất nước.</div>\n<h3><span style="color: rgb(107, 142, 35);">Lịch sử Campuchia :</span></h3>\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Phù Nam : (Đầu thế kỷ 1 đến năm 550)</span></div>\n<div><br>\n	</div>\n<div>Được biết đến đất nước Campuchia đầu tiên có tên nước là Phù Nam vào khoảng thế kỷ thứ 1, triệu đại Phù Nam đầu tiên qua 4 đời kế vị đó là :&nbsp;Hỗn Điền, Con Hỗn Điền (chưa rõ tên họ), Hỗn Bàn Huống, Hỗn Bàn Bàn, kế tiếp là một vị tướng đã lên ngôi đó là Phạm Sư Man -&gt; Phạm Chiêu -&gt; Phạm Tràng -&gt; Phạm Tầm,..cho đến giữa thế kỷ thứ 7 đất nước Phù Nam chấm dứt do nước Chân Lạp xâm lược.</div>\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);"><br>\n	</span></div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Chân Lạp : ( Năm 550 đến năm 802)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Là một quốc gia do người Khmer sáng lập vào thế kỷ thứ 7 nằm ở Sae Mun (nay thuộc Thái Lan) và Champasack (nay thuộc Nam Lào). Quốc gia này do Bhavavarman sáng lập trong thế kỷ thứ 6, gọi là nước Bhavapura, tức Chân Lạp. Sau khi đánh thắng Phù Nam người Chân Lạp đã ồ ạt di cư xuống phía Nam và dừng chân định cư tại Takeo (cụm di tích Angkor Borey) và Prey Veng (cụm di tích Ba Phnom), trung lưu sông Mekong và Đông Bắc biển Hồ. Isanavarman đã xây dựng kinh đô Isanapura ở gần Kompong Thom.&nbsp;Sau năm 707, Chân Lạp tách thành hai quốc gia Lục Chân Lạp và Thủy Chân Lạp -&gt; Đây chính là thời kỳ khủng hoảng của vương quốc Chân Lạp</div>\n\n\n\n\n\n\n<p><span style="font-weight: bold; color: rgb(0, 0, 205);">Đế quốc Khmer : (Năm 802 đến 1432)</span></p>\n\n\n\n\n\n\n<div>Đầu thế kỷ thứ 9 tức năm 802 vua&nbsp;Jayavarman II đã thống nhất đất nước Campuchia tạo ra một đế chế Khmer hùng mạnh còn gọi là "vương quốc Angkor". Thời kỳ phát triển của đế chế này vào năm&nbsp;944 đến 1181, thời kỳ cực thịnh năm 1181 đến năm 1201, và suy thoái dần từ năm&nbsp;1201 đến năm 1432.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Thời kỳ hậu Angkor : (Năm 1432 đến năm&nbsp;1863)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Từ giữ thế kỷ 15 vương quốc Angkok bị liên tục tấn công bởi&nbsp;Ayutthaya (Thái Lan cổ), đến cuối thế kỷ thứ 17 Angkok chịu sức ép giữ hai nước đang bành trướng đó là Đại Việt và Ayutthaya, đến năm 1863 có người Pháp vào bảo hộ cho vương quốc Angkok, chính vì thề mà vương quốc Angkok đã bị lệ thuộc.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Campuchia thuộc Pháp :&nbsp;(Năm 1863 đến năm 1953)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Năm 1863 Vua Norodom ký một hiệp ước với Pháp để thành lập một chính quyền bảo hộ trên toàn vương quốc.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Campuchia : (Năm 1954 đến năm 1975)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Campuchia tuyên bố độc lập vào năm 1953, trở thành một vương quốc. Năm 1960, Thái tử Norodom Sihanouk lên làm Quốc trưởng chứ không làm vua sau khi vua cha mất. Ông thi hành chính sách trung lập.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Khmer Đỏ cầm quyền: (Năm 1975 đến năm 1979)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Năm 1975&nbsp;thành lập nước "Campuchia Dân chủ" do&nbsp;Khmer Đỏ do Pol Pot lãnh đạo nắm được chính quyền.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Cộng hòa Nhân dân Campuchia (Năm 1979 đến năm 1993)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Ngày 8 tháng 1 năm 1979 Hội đồng Nhân dân Cách mạng nước Cộng hòa Nhân dân Campuchia do Heng Samrin làm Chủ tịch đã được thành lập. Tuy vậy chính quyền này chỉ được một số nước cộng sản công nhận và chưa tự bảo vệ được mà vẫn cần đến sự có mặt của quân đội Việt Nam.</div>\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Campuchia (Tái lập năm 1993 - hiện tại)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n\n\n\n<div>Sau khi cuộc Tổng tuyển cử tự do được Liên Hợp Quốc đứng ra tổ chức tháng 5 năm 1993, các phe phái, chủ yếu các CPP (Đảng Nhân dân Campuchia), FUNCIPEC (Đảng Bảo hoàng)và đảng của Sam Rensi phải mất đến 4 tháng mới thỏa thuận được cơ cấu phân chia quyền lực. Đến tháng 9 năm 1993, Quốc hội và Chính phủ mới được thành lập với nòng cốt là FUNCIPEC và CPP. Quốc hội nhất trí lấy tên nước là Vương quốc Campuchia. Đứng đầu nhà nước là Quốc vương Norodom Sihanuk. Thủ tướng thứ nhất Norodom Ranarit (Chủ tịch Đảng FUNCIPEC), Thủ tướng thứ hai là Hun Xen (Phó Chủ tịch Đảng Nhân dân Campuchia). Chủ tịch quốc hội là Chia Xim (CPP).</div>\n\n\n\n\n<div>Vương quốc Campuchia mở rộng đối ngoại, đối nội, phát triển công nghiệp hóa, nông nghiệp hóa, giao thương với các nước trên thế giới mở rộng tìm nâng cho sự phát triển kinh tế trong tương lai.</div>\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	</div>\n\n\n\n\n<div>Hiện nay vương quốc Campuchia trong thời kỳ phát triển mạnh mẻ đặc biệt là ngành du lịch đa thu hút một lượng khách du lịch quốc tế rất lớn và là một trong những địa điểm du lịch nổi tiếng thế giới.</div>\n\n\n\n\n<div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Thời tiết và khí hậu Campuchia:</span></h3>\n	\n	\n	\n	<div><br>\n		\n		\n		\n		</div>\n	\n	\n	\n	<div>- Thời tiết tại Campuchia có hai mùa rõ rệt, mùa nắng và mùa mưa, mùa mưa kéo dài từ tháng 5 đến tháng 10, mùa mưa kéo dài từ tháng 11 đến tháng 4.</div>\n	\n	\n	\n	<div>- Khí hậu gió mùa nên trở thành vùng nhiệt đới ẩm và khô rõ rệt, nhiệt độ trung bình từ 25 đến 28 độ C, nhiệt độ thấp trung bình là 22 độ C, nhiệt độ cao trung bình là 32 độ C, tuy nhiên có lúc nhiệt độ lên cao đến tận 38 độ C và thấp nhất là 10 độ C nhưng đây là một số năm khí hậu hiếm thấy.</div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Dân tộc và ngôn ngữ Campuchia :&nbsp;</span></h3>\n	\n	\n	\n	<div>Đa phần là dân dộc người Khmer chiếm 90%, dân tộc người Khmer chi thành nhiều loại :&nbsp;Khmer giữa (Khmer Kandal),&nbsp;&nbsp;Khmer Thượng (Khmer Loeur) và Khmer Hạ (Khmer Krom), còn lại là 10% thuộc các dân tộc từ nước khác sang như :&nbsp;Mã Lai, Chàm, Lào, Miến Điện, Việt Nam, Thái Lan, Hoa,.</div>\n	\n	\n	\n	<div>Tiếng Khmer là ngôn ngữ chính (95%), còn lại 5% là được sử dụng tiếng Anh, Pháp Việt, Mã Lai, Trung Hoa,..</div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Du Lịch Campuchia :</span></h3></div>\n\n\n\n\n<div>Sau một thời gian dài 25 năm cô lập, Campuchia mở rộng đón các du khách nước ngoài từ năm 90 và lượng khách tăng dần theo mỗi năm đến thời điểm hiện nay Vương Quốc Campuchia trở thành một địa điểm du lịch hấp dẫn nhất thế giới, những địa điểm thu hút nhất là quần thể Angkok nằm ở Siem Riep, thủ đô&nbsp;Phnom Penh và biển Sihanuk, đấy chính là những địa điểm thu hút khách quốc tế nhất.\n	\n	\n	</div>\n\n<div><br>\n	\n	</div>\n\n<div><span style="font-weight: bold;">Sau đây là những địa điểm du lịch Campuchia :</span></div>\n\n<div><br>\n	\n	</div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/angkor-wat-tai-campuchia.html">Angkor Wat</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/angkor-thom-tai-campuchia.html">Angkor Thom</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/chua-vang-chua-bac-tai-campuchia.html">Chùa vàng - Chùa Bạc</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/tuong-dai-huu-nghi-viet-nam-campuchia.html">Đài tưởng niệm Việt Nam – Cambodia</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;Bãi biển Sihanoukville.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/bao-tang-quoc-gia-tai-campuchia.html">Bảo tàng quốc gia Campuchia</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/nui-ta-lon-tai-campuchia.html">Núi Tà Lơn (Bokor)</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/ngoi-den-banteay-srei-tai-campuchia.html">Ngôi đền Banteay Srei</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/co-do-oudong-tai-campuchia.html">Cố Đô Oudong</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/cung-dien-hoang-gia-tai-campuchia.html">Cung Điện Hoàng Gia</a>.</span></div>\n\n<div><span style="font-weight: bold;">- <a href="http://toptravels.vn/chua-wat-phnom-tai-campuchia.html">Chùa Wat Phnom</a></span></div>\n\n<div>Và một số địa điểm khác nữa.</div>\n\n<div><br>\n	\n	</div>\n\n<div><br>\n	\n	</div>\n', 16, 1),
(7, 'NHA TRANG + DỐC LẾT + HÒN TẰM', 'Ô tô', '2014-12-30', '2015-01-02', 2892000, 'Biển Nha Trang tuyệt vời với Vinpearl Nha Trang 5* sang trọng, hòn Mun Hòn Tằm nước trong veo và san hô lộng lẫy, cùng với vịnh Ninh Vân, vịnh Vân Phong hoang sơ và thuần khiết. Viện Hải dương học Nha Trang có trên 20.000 mẫu sinh vật dưới nước, tháp Bà Ponagar hoàn mỹ của người Chăm, cảng Vũng Rô,... ', '260614102246.jpg', '\n<h1 style="text-align: center;"><span style="color: red;">DU LỊCH NHA TRANG</span></h1>\n<div>Campuchia là một quốc gia thuộc Đông Nam Á, nổi tiếng là một quốc gia có nhiều chùa, đền đài và đặc biệt là công trình kiến trúc độc đáo và đầy huyền bí Angkok Wat và Angkok Thom đã thu hút hàng triệu du khách quốc tế hàng năm và hiện tại Campuchia đã trở thành một địa điểm du lịch hấp dẫn nhất thế giới.</div>\n<div><br>\n	</div>\n<div style="text-align: center;">\n	<img src="image_data/tournuocngoai/1/1-1.jpg" alt="vuong-quoc-campuchia" align="" border="0px"></div>\n<h3><span style="color: rgb(107, 142, 35);">Vị trí địa lý Campuchia:</span></h3>\n<div>Vương quốc Campuchia có diện tích khoảng 181.035 km2, phía Bắc giáp với Lào có đường biên giới là 541 km, phía Đông giáp với Việt Nam có đường biên giới là 1.137 km, phía Tây giáp Thái Lan và phía nam giáp với Vịnh Thái Lan có đường biên giới là 800 km.&nbsp;Đồng bằng chiếm 1/2 diện tích tập trung ở hướng Nam và Đông Nam, còn lại là núi, đồi bao quanh đất nước.</div>\n<h3><span style="color: rgb(107, 142, 35);">Lịch sử Campuchia :</span></h3>\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Phù Nam : (Đầu thế kỷ 1 đến năm 550)</span></div>\n<div><br>\n	</div>\n<div>Được biết đến đất nước Campuchia đầu tiên có tên nước là Phù Nam vào khoảng thế kỷ thứ 1, triệu đại Phù Nam đầu tiên qua 4 đời kế vị đó là :&nbsp;Hỗn Điền, Con Hỗn Điền (chưa rõ tên họ), Hỗn Bàn Huống, Hỗn Bàn Bàn, kế tiếp là một vị tướng đã lên ngôi đó là Phạm Sư Man -&gt; Phạm Chiêu -&gt; Phạm Tràng -&gt; Phạm Tầm,..cho đến giữa thế kỷ thứ 7 đất nước Phù Nam chấm dứt do nước Chân Lạp xâm lược.</div>\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);"><br>\n	</span></div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Chân Lạp : ( Năm 550 đến năm 802)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Là một quốc gia do người Khmer sáng lập vào thế kỷ thứ 7 nằm ở Sae Mun (nay thuộc Thái Lan) và Champasack (nay thuộc Nam Lào). Quốc gia này do Bhavavarman sáng lập trong thế kỷ thứ 6, gọi là nước Bhavapura, tức Chân Lạp. Sau khi đánh thắng Phù Nam người Chân Lạp đã ồ ạt di cư xuống phía Nam và dừng chân định cư tại Takeo (cụm di tích Angkor Borey) và Prey Veng (cụm di tích Ba Phnom), trung lưu sông Mekong và Đông Bắc biển Hồ. Isanavarman đã xây dựng kinh đô Isanapura ở gần Kompong Thom.&nbsp;Sau năm 707, Chân Lạp tách thành hai quốc gia Lục Chân Lạp và Thủy Chân Lạp -&gt; Đây chính là thời kỳ khủng hoảng của vương quốc Chân Lạp</div>\n\n\n\n\n\n\n<p><span style="font-weight: bold; color: rgb(0, 0, 205);">Đế quốc Khmer : (Năm 802 đến 1432)</span></p>\n\n\n\n\n\n\n<div>Đầu thế kỷ thứ 9 tức năm 802 vua&nbsp;Jayavarman II đã thống nhất đất nước Campuchia tạo ra một đế chế Khmer hùng mạnh còn gọi là "vương quốc Angkor". Thời kỳ phát triển của đế chế này vào năm&nbsp;944 đến 1181, thời kỳ cực thịnh năm 1181 đến năm 1201, và suy thoái dần từ năm&nbsp;1201 đến năm 1432.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Thời kỳ hậu Angkor : (Năm 1432 đến năm&nbsp;1863)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Từ giữ thế kỷ 15 vương quốc Angkok bị liên tục tấn công bởi&nbsp;Ayutthaya (Thái Lan cổ), đến cuối thế kỷ thứ 17 Angkok chịu sức ép giữ hai nước đang bành trướng đó là Đại Việt và Ayutthaya, đến năm 1863 có người Pháp vào bảo hộ cho vương quốc Angkok, chính vì thề mà vương quốc Angkok đã bị lệ thuộc.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Campuchia thuộc Pháp :&nbsp;(Năm 1863 đến năm 1953)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Năm 1863 Vua Norodom ký một hiệp ước với Pháp để thành lập một chính quyền bảo hộ trên toàn vương quốc.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Campuchia : (Năm 1954 đến năm 1975)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Campuchia tuyên bố độc lập vào năm 1953, trở thành một vương quốc. Năm 1960, Thái tử Norodom Sihanouk lên làm Quốc trưởng chứ không làm vua sau khi vua cha mất. Ông thi hành chính sách trung lập.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Khmer Đỏ cầm quyền: (Năm 1975 đến năm 1979)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Năm 1975&nbsp;thành lập nước "Campuchia Dân chủ" do&nbsp;Khmer Đỏ do Pol Pot lãnh đạo nắm được chính quyền.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Cộng hòa Nhân dân Campuchia (Năm 1979 đến năm 1993)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Ngày 8 tháng 1 năm 1979 Hội đồng Nhân dân Cách mạng nước Cộng hòa Nhân dân Campuchia do Heng Samrin làm Chủ tịch đã được thành lập. Tuy vậy chính quyền này chỉ được một số nước cộng sản công nhận và chưa tự bảo vệ được mà vẫn cần đến sự có mặt của quân đội Việt Nam.</div>\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Campuchia (Tái lập năm 1993 - hiện tại)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n\n\n\n<div>Sau khi cuộc Tổng tuyển cử tự do được Liên Hợp Quốc đứng ra tổ chức tháng 5 năm 1993, các phe phái, chủ yếu các CPP (Đảng Nhân dân Campuchia), FUNCIPEC (Đảng Bảo hoàng)và đảng của Sam Rensi phải mất đến 4 tháng mới thỏa thuận được cơ cấu phân chia quyền lực. Đến tháng 9 năm 1993, Quốc hội và Chính phủ mới được thành lập với nòng cốt là FUNCIPEC và CPP. Quốc hội nhất trí lấy tên nước là Vương quốc Campuchia. Đứng đầu nhà nước là Quốc vương Norodom Sihanuk. Thủ tướng thứ nhất Norodom Ranarit (Chủ tịch Đảng FUNCIPEC), Thủ tướng thứ hai là Hun Xen (Phó Chủ tịch Đảng Nhân dân Campuchia). Chủ tịch quốc hội là Chia Xim (CPP).</div>\n\n\n\n\n<div>Vương quốc Campuchia mở rộng đối ngoại, đối nội, phát triển công nghiệp hóa, nông nghiệp hóa, giao thương với các nước trên thế giới mở rộng tìm nâng cho sự phát triển kinh tế trong tương lai.</div>\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	</div>\n\n\n\n\n<div>Hiện nay vương quốc Campuchia trong thời kỳ phát triển mạnh mẻ đặc biệt là ngành du lịch đa thu hút một lượng khách du lịch quốc tế rất lớn và là một trong những địa điểm du lịch nổi tiếng thế giới.</div>\n\n\n\n\n<div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Thời tiết và khí hậu Campuchia:</span></h3>\n	\n	\n	\n	<div><br>\n		\n		\n		\n		</div>\n	\n	\n	\n	<div>- Thời tiết tại Campuchia có hai mùa rõ rệt, mùa nắng và mùa mưa, mùa mưa kéo dài từ tháng 5 đến tháng 10, mùa mưa kéo dài từ tháng 11 đến tháng 4.</div>\n	\n	\n	\n	<div>- Khí hậu gió mùa nên trở thành vùng nhiệt đới ẩm và khô rõ rệt, nhiệt độ trung bình từ 25 đến 28 độ C, nhiệt độ thấp trung bình là 22 độ C, nhiệt độ cao trung bình là 32 độ C, tuy nhiên có lúc nhiệt độ lên cao đến tận 38 độ C và thấp nhất là 10 độ C nhưng đây là một số năm khí hậu hiếm thấy.</div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Dân tộc và ngôn ngữ Campuchia :&nbsp;</span></h3>\n	\n	\n	\n	<div>Đa phần là dân dộc người Khmer chiếm 90%, dân tộc người Khmer chi thành nhiều loại :&nbsp;Khmer giữa (Khmer Kandal),&nbsp;&nbsp;Khmer Thượng (Khmer Loeur) và Khmer Hạ (Khmer Krom), còn lại là 10% thuộc các dân tộc từ nước khác sang như :&nbsp;Mã Lai, Chàm, Lào, Miến Điện, Việt Nam, Thái Lan, Hoa,.</div>\n	\n	\n	\n	<div>Tiếng Khmer là ngôn ngữ chính (95%), còn lại 5% là được sử dụng tiếng Anh, Pháp Việt, Mã Lai, Trung Hoa,..</div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Du Lịch Campuchia :</span></h3></div>\n\n\n\n\n<div>Sau một thời gian dài 25 năm cô lập, Campuchia mở rộng đón các du khách nước ngoài từ năm 90 và lượng khách tăng dần theo mỗi năm đến thời điểm hiện nay Vương Quốc Campuchia trở thành một địa điểm du lịch hấp dẫn nhất thế giới, những địa điểm thu hút nhất là quần thể Angkok nằm ở Siem Riep, thủ đô&nbsp;Phnom Penh và biển Sihanuk, đấy chính là những địa điểm thu hút khách quốc tế nhất.\n	\n	\n	</div>\n\n<div><br>\n	\n	</div>\n\n<div><span style="font-weight: bold;">Sau đây là những địa điểm du lịch Campuchia :</span></div>\n\n<div><br>\n	\n	</div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/angkor-wat-tai-campuchia.html">Angkor Wat</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/angkor-thom-tai-campuchia.html">Angkor Thom</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/chua-vang-chua-bac-tai-campuchia.html">Chùa vàng - Chùa Bạc</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/tuong-dai-huu-nghi-viet-nam-campuchia.html">Đài tưởng niệm Việt Nam – Cambodia</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;Bãi biển Sihanoukville.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/bao-tang-quoc-gia-tai-campuchia.html">Bảo tàng quốc gia Campuchia</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/nui-ta-lon-tai-campuchia.html">Núi Tà Lơn (Bokor)</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/ngoi-den-banteay-srei-tai-campuchia.html">Ngôi đền Banteay Srei</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/co-do-oudong-tai-campuchia.html">Cố Đô Oudong</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/cung-dien-hoang-gia-tai-campuchia.html">Cung Điện Hoàng Gia</a>.</span></div>\n\n<div><span style="font-weight: bold;">- <a href="http://toptravels.vn/chua-wat-phnom-tai-campuchia.html">Chùa Wat Phnom</a></span></div>\n\n<div>Và một số địa điểm khác nữa.</div>\n\n<div><br>\n	\n	</div>\n\n<div><br>\n	\n	</div>\n', 16, 1);
INSERT INTO `tour` (`Ma_tour`, `Ten_tour`, `Phuong_tien`, `Ngay_bat_dau`, `Ngay_ket_thuc`, `Gia`, `Noi_dung`, `Hinh_anh`, `Noi_dung2`, `So_cho_toi_da`, `AnHien`) VALUES
(8, 'Du Lịch Phan Thiết - Tour Biễn Xanh Dậy Sóng', 'Ô tô', '2014-12-30', '2014-12-31', 1398000, 'Phan Thiết hấp dẫn với Lầu Ông Hoàng, tháp Chàm Pôshanư, bãi đá Ông Địa, rạn dừa Hàm Tiến. KDL Hòn Rơm cát trắng bên hàng dương xanh. Trượt cát trên Đồi Cát Bay, ngắm hoàng hôn trên đỉnh Đồi Hồng là nguồn cảm hứng bất tận của các nhiếp ảnh gia. Di tích lịch sử ở Phan Thiết là trường Dục Thanh nơi Bác Hồ từng dạy học... ', 'ThumbnailHandler.jpg', '\n<h1 style="text-align: center;"><span style="color: red;">DU LỊCH PHAN THIẾT</span></h1>\n<div>Campuchia là một quốc gia thuộc Đông Nam Á, nổi tiếng là một quốc gia có nhiều chùa, đền đài và đặc biệt là công trình kiến trúc độc đáo và đầy huyền bí Angkok Wat và Angkok Thom đã thu hút hàng triệu du khách quốc tế hàng năm và hiện tại Campuchia đã trở thành một địa điểm du lịch hấp dẫn nhất thế giới.</div>\n<div><br>\n	</div>\n<div style="text-align: center;">\n	<img src="image_data/tournuocngoai/1/1-1.jpg" alt="vuong-quoc-campuchia" align="" border="0px"></div>\n<h3><span style="color: rgb(107, 142, 35);">Vị trí địa lý Campuchia:</span></h3>\n<div>Vương quốc Campuchia có diện tích khoảng 181.035 km2, phía Bắc giáp với Lào có đường biên giới là 541 km, phía Đông giáp với Việt Nam có đường biên giới là 1.137 km, phía Tây giáp Thái Lan và phía nam giáp với Vịnh Thái Lan có đường biên giới là 800 km.&nbsp;Đồng bằng chiếm 1/2 diện tích tập trung ở hướng Nam và Đông Nam, còn lại là núi, đồi bao quanh đất nước.</div>\n<h3><span style="color: rgb(107, 142, 35);">Lịch sử Campuchia :</span></h3>\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Phù Nam : (Đầu thế kỷ 1 đến năm 550)</span></div>\n<div><br>\n	</div>\n<div>Được biết đến đất nước Campuchia đầu tiên có tên nước là Phù Nam vào khoảng thế kỷ thứ 1, triệu đại Phù Nam đầu tiên qua 4 đời kế vị đó là :&nbsp;Hỗn Điền, Con Hỗn Điền (chưa rõ tên họ), Hỗn Bàn Huống, Hỗn Bàn Bàn, kế tiếp là một vị tướng đã lên ngôi đó là Phạm Sư Man -&gt; Phạm Chiêu -&gt; Phạm Tràng -&gt; Phạm Tầm,..cho đến giữa thế kỷ thứ 7 đất nước Phù Nam chấm dứt do nước Chân Lạp xâm lược.</div>\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);"><br>\n	</span></div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Chân Lạp : ( Năm 550 đến năm 802)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Là một quốc gia do người Khmer sáng lập vào thế kỷ thứ 7 nằm ở Sae Mun (nay thuộc Thái Lan) và Champasack (nay thuộc Nam Lào). Quốc gia này do Bhavavarman sáng lập trong thế kỷ thứ 6, gọi là nước Bhavapura, tức Chân Lạp. Sau khi đánh thắng Phù Nam người Chân Lạp đã ồ ạt di cư xuống phía Nam và dừng chân định cư tại Takeo (cụm di tích Angkor Borey) và Prey Veng (cụm di tích Ba Phnom), trung lưu sông Mekong và Đông Bắc biển Hồ. Isanavarman đã xây dựng kinh đô Isanapura ở gần Kompong Thom.&nbsp;Sau năm 707, Chân Lạp tách thành hai quốc gia Lục Chân Lạp và Thủy Chân Lạp -&gt; Đây chính là thời kỳ khủng hoảng của vương quốc Chân Lạp</div>\n\n\n\n\n\n\n<p><span style="font-weight: bold; color: rgb(0, 0, 205);">Đế quốc Khmer : (Năm 802 đến 1432)</span></p>\n\n\n\n\n\n\n<div>Đầu thế kỷ thứ 9 tức năm 802 vua&nbsp;Jayavarman II đã thống nhất đất nước Campuchia tạo ra một đế chế Khmer hùng mạnh còn gọi là "vương quốc Angkor". Thời kỳ phát triển của đế chế này vào năm&nbsp;944 đến 1181, thời kỳ cực thịnh năm 1181 đến năm 1201, và suy thoái dần từ năm&nbsp;1201 đến năm 1432.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Thời kỳ hậu Angkor : (Năm 1432 đến năm&nbsp;1863)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Từ giữ thế kỷ 15 vương quốc Angkok bị liên tục tấn công bởi&nbsp;Ayutthaya (Thái Lan cổ), đến cuối thế kỷ thứ 17 Angkok chịu sức ép giữ hai nước đang bành trướng đó là Đại Việt và Ayutthaya, đến năm 1863 có người Pháp vào bảo hộ cho vương quốc Angkok, chính vì thề mà vương quốc Angkok đã bị lệ thuộc.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Campuchia thuộc Pháp :&nbsp;(Năm 1863 đến năm 1953)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Năm 1863 Vua Norodom ký một hiệp ước với Pháp để thành lập một chính quyền bảo hộ trên toàn vương quốc.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Campuchia : (Năm 1954 đến năm 1975)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Campuchia tuyên bố độc lập vào năm 1953, trở thành một vương quốc. Năm 1960, Thái tử Norodom Sihanouk lên làm Quốc trưởng chứ không làm vua sau khi vua cha mất. Ông thi hành chính sách trung lập.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Khmer Đỏ cầm quyền: (Năm 1975 đến năm 1979)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Năm 1975&nbsp;thành lập nước "Campuchia Dân chủ" do&nbsp;Khmer Đỏ do Pol Pot lãnh đạo nắm được chính quyền.</div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Cộng hòa Nhân dân Campuchia (Năm 1979 đến năm 1993)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n<div>Ngày 8 tháng 1 năm 1979 Hội đồng Nhân dân Cách mạng nước Cộng hòa Nhân dân Campuchia do Heng Samrin làm Chủ tịch đã được thành lập. Tuy vậy chính quyền này chỉ được một số nước cộng sản công nhận và chưa tự bảo vệ được mà vẫn cần đến sự có mặt của quân đội Việt Nam.</div>\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n<div><span style="font-weight: bold; color: rgb(0, 0, 205);">Vương quốc Campuchia (Tái lập năm 1993 - hiện tại)</span></div>\n\n\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	\n	\n	</div>\n\n\n\n\n\n\n\n\n\n<div>Sau khi cuộc Tổng tuyển cử tự do được Liên Hợp Quốc đứng ra tổ chức tháng 5 năm 1993, các phe phái, chủ yếu các CPP (Đảng Nhân dân Campuchia), FUNCIPEC (Đảng Bảo hoàng)và đảng của Sam Rensi phải mất đến 4 tháng mới thỏa thuận được cơ cấu phân chia quyền lực. Đến tháng 9 năm 1993, Quốc hội và Chính phủ mới được thành lập với nòng cốt là FUNCIPEC và CPP. Quốc hội nhất trí lấy tên nước là Vương quốc Campuchia. Đứng đầu nhà nước là Quốc vương Norodom Sihanuk. Thủ tướng thứ nhất Norodom Ranarit (Chủ tịch Đảng FUNCIPEC), Thủ tướng thứ hai là Hun Xen (Phó Chủ tịch Đảng Nhân dân Campuchia). Chủ tịch quốc hội là Chia Xim (CPP).</div>\n\n\n\n\n<div>Vương quốc Campuchia mở rộng đối ngoại, đối nội, phát triển công nghiệp hóa, nông nghiệp hóa, giao thương với các nước trên thế giới mở rộng tìm nâng cho sự phát triển kinh tế trong tương lai.</div>\n\n\n\n\n<div><br>\n	\n	\n	\n	\n	</div>\n\n\n\n\n<div>Hiện nay vương quốc Campuchia trong thời kỳ phát triển mạnh mẻ đặc biệt là ngành du lịch đa thu hút một lượng khách du lịch quốc tế rất lớn và là một trong những địa điểm du lịch nổi tiếng thế giới.</div>\n\n\n\n\n<div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Thời tiết và khí hậu Campuchia:</span></h3>\n	\n	\n	\n	<div><br>\n		\n		\n		\n		</div>\n	\n	\n	\n	<div>- Thời tiết tại Campuchia có hai mùa rõ rệt, mùa nắng và mùa mưa, mùa mưa kéo dài từ tháng 5 đến tháng 10, mùa mưa kéo dài từ tháng 11 đến tháng 4.</div>\n	\n	\n	\n	<div>- Khí hậu gió mùa nên trở thành vùng nhiệt đới ẩm và khô rõ rệt, nhiệt độ trung bình từ 25 đến 28 độ C, nhiệt độ thấp trung bình là 22 độ C, nhiệt độ cao trung bình là 32 độ C, tuy nhiên có lúc nhiệt độ lên cao đến tận 38 độ C và thấp nhất là 10 độ C nhưng đây là một số năm khí hậu hiếm thấy.</div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Dân tộc và ngôn ngữ Campuchia :&nbsp;</span></h3>\n	\n	\n	\n	<div>Đa phần là dân dộc người Khmer chiếm 90%, dân tộc người Khmer chi thành nhiều loại :&nbsp;Khmer giữa (Khmer Kandal),&nbsp;&nbsp;Khmer Thượng (Khmer Loeur) và Khmer Hạ (Khmer Krom), còn lại là 10% thuộc các dân tộc từ nước khác sang như :&nbsp;Mã Lai, Chàm, Lào, Miến Điện, Việt Nam, Thái Lan, Hoa,.</div>\n	\n	\n	\n	<div>Tiếng Khmer là ngôn ngữ chính (95%), còn lại 5% là được sử dụng tiếng Anh, Pháp Việt, Mã Lai, Trung Hoa,..</div>\n	\n	\n	\n	<h3><span style="color: rgb(107, 142, 35);">Du Lịch Campuchia :</span></h3></div>\n\n\n\n\n<div>Sau một thời gian dài 25 năm cô lập, Campuchia mở rộng đón các du khách nước ngoài từ năm 90 và lượng khách tăng dần theo mỗi năm đến thời điểm hiện nay Vương Quốc Campuchia trở thành một địa điểm du lịch hấp dẫn nhất thế giới, những địa điểm thu hút nhất là quần thể Angkok nằm ở Siem Riep, thủ đô&nbsp;Phnom Penh và biển Sihanuk, đấy chính là những địa điểm thu hút khách quốc tế nhất.\n	\n	\n	</div>\n\n<div><br>\n	\n	</div>\n\n<div><span style="font-weight: bold;">Sau đây là những địa điểm du lịch Campuchia :</span></div>\n\n<div><br>\n	\n	</div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/angkor-wat-tai-campuchia.html">Angkor Wat</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/angkor-thom-tai-campuchia.html">Angkor Thom</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/chua-vang-chua-bac-tai-campuchia.html">Chùa vàng - Chùa Bạc</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/tuong-dai-huu-nghi-viet-nam-campuchia.html">Đài tưởng niệm Việt Nam – Cambodia</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;Bãi biển Sihanoukville.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/bao-tang-quoc-gia-tai-campuchia.html">Bảo tàng quốc gia Campuchia</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/nui-ta-lon-tai-campuchia.html">Núi Tà Lơn (Bokor)</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/ngoi-den-banteay-srei-tai-campuchia.html">Ngôi đền Banteay Srei</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/co-do-oudong-tai-campuchia.html">Cố Đô Oudong</a>.</span></div>\n\n<div><span style="font-weight: bold;">-&nbsp;<a href="http://toptravels.vn/cung-dien-hoang-gia-tai-campuchia.html">Cung Điện Hoàng Gia</a>.</span></div>\n\n<div><span style="font-weight: bold;">- <a href="http://toptravels.vn/chua-wat-phnom-tai-campuchia.html">Chùa Wat Phnom</a></span></div>\n\n<div>Và một số địa điểm khác nữa.</div>\n\n<div><br>\n	\n	</div>\n\n<div><br>\n	\n	</div>\n', 32, 1),
(9, 'Tour Du Ngoạn Thái Lan', 'Ô tô', '2014-12-31', '2015-01-05', 5850000, 'Trưởng đoàn của Cty sẽ đón quý khách tại sân bay Tân Sơn Nhất,Quốc Tế .Sau đó trưởng đoàn làm thủ tục để đáp chuyến bay đi Thái Lan. Đến sân bay Suvarnabhumi  của vương quốc Thái.Sau đó xe đưa Quý khách về khách sạn nhận phòng nghỉ ngơi hoặc tự do khám phá Bangkok về đêm…', 'Du-Lich-Thai-Lan.jpg', '<h1 style="text-align: center;"><strong>Tour Du Ngoạn Th&aacute;i Lan</strong></h1>\n\n<p><strong>NG&Agrave;Y 01: TP. HỒ CH&Iacute; MINH &ndash; BANGKOK (Ăn nhẹ tr&ecirc;n m&aacute;y bay)</strong></p>\n\n<p>Trưởng đo&agrave;n của Cty&nbsp;sẽ đ&oacute;n qu&yacute; kh&aacute;ch tại s&acirc;n bay T&acirc;n Sơn Nhất,Quốc Tế .Sau đ&oacute; trưởng đo&agrave;n l&agrave;m thủ tục để đ&aacute;p chuyến bay đi Th&aacute;i Lan. Đến s&acirc;n bay Suvarnabhumi &nbsp;của vương quốc Th&aacute;i.Sau đ&oacute; xe đưa Qu&yacute; kh&aacute;ch về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi hoặc tự do kh&aacute;m ph&aacute; Bangkok về đ&ecirc;m&hellip;&nbsp;</p>\n\n<p><img alt="" src="http://dulichkhonggian.vn/ckfinder/userfiles/images/Image_Tour_Nuoc_Ngoai/Du-Lich-Thai-Lan-Kham-Pha-Dat-Nuoc-Thien-Than---Trung-Tam-Mua-Sam-Dong-Nam-A-2013-05-22-15-44-46.jpg" style="height:307px; width:430px" /></p>\n\n<p>&nbsp;</p>\n\n<p><strong>NG&Agrave;Y 02: BANGKOK &ndash; PATTAYA (Ăn s&aacute;ng, trưa, chiều)</strong></p>\n\n<p>Dạo thuyền tr&ecirc;n d&ograve;ng s&ocirc;ng Chaophraya Huyền thoại. Xem thuyền Rồng của nh&agrave; vua, xem hiện tượng c&aacute; nổi tr&ecirc;n s&ocirc;ng.</p>\n\n<p>Tham quan Ho&agrave;ng Cung Ngũ Thế Ho&agrave;ng, được vua Rama V x&acirc;y dựng v&agrave;o năm 1901,v&agrave; từ đ&oacute; đến nay vẫn lu&ocirc;n được bảo tồn cẩn thận để quảng b&aacute; sự huy ho&agrave;ng v&agrave; thịnh vượng của một thời k&igrave; ho&agrave;ng kim.- Đ&acirc;y l&agrave; t&ograve;a nh&agrave; bằng gỗ Teak m&agrave;u v&agrave;ng đẹp v&agrave; lớn nhất thế giới,trong Ho&agrave;ng Cung trưng b&agrave;y c&aacute;c t&aacute;c phẩm như: Ng&agrave; voi &ndash; Thủy Tinh &ndash; Đồ vật bằng bạc &ndash; Đồ s&agrave;nh sứ - Đồ cổ&hellip;.&nbsp;</p>\n\n<p>Sau đ&oacute; xe đi thẳng về th&agrave;nh phố ma quỷ Pattaya,&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><img alt="" src="http://dulichkhonggian.vn/ckfinder/userfiles/images/Image_Tour_Nuoc_Ngoai/du-lich-Thai-Lan.jpg" style="height:318px; width:500px" /></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&lsquo;&rsquo;Đồi vọng cảnh Phra Tamak&rsquo;&rsquo;, tại đ&acirc;y Du Kh&aacute;ch thỏa sức ngắm cảnh tr&ecirc;n biển Pattaya v&agrave; chi&ecirc;m ngưỡng vẻ đẹp lộng lẫy của to&agrave;n cảnh th&agrave;nh phố nhộn nhịp n&agrave;y. Ngo&agrave;i ra Du Kh&aacute;ch c&oacute; thể chi&ecirc;m b&aacute;i, đốt ph&aacute;o cầu ph&uacute;c, cầu may để tỏ l&ograve;ng t&ocirc;n k&iacute;nh tại đ&agrave;i tưởng niệm của Đại Tướng Krom Luang Chumporn, cha đẻ của ng&agrave;nh hải qu&acirc;n ho&agrave;ng gia Th&aacute;i Lan. Được người d&acirc;n Th&aacute;i t&ocirc;n k&iacute;nh với l&ograve;ng dũng cảm v&agrave; ngay thẳng của &Ocirc;ng.</p>\n\n<p>khi đến nơi Qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối, về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi hoặc tham gia c&aacute;c show về đ&ecirc;m tại Pataya (chi ph&iacute; tự t&uacute;c)&hellip;</p>\n\n<p>&nbsp;</p>\n\n<p><img alt="" src="http://dulichkhonggian.vn/ckfinder/userfiles/images/Image_Tour_Nuoc_Ngoai/2001142633-du-lich-pattaya.jpg" style="height:303px; width:500px" /></p>\n\n<p>&nbsp;</p>\n\n<p><strong>NG&Agrave;Y 03: THAM QUAN PATTAYA - ĐẢO CORAL (Ăn s&aacute;ng, trưa, chiều)</strong></p>\n\n<p>Sau khi ăn s&aacute;ng, Qu&yacute; kh&aacute;ch khởi h&agrave;nh đi Đảo San H&ocirc;(Coral) Bằng t&agrave;u cao tốc, tại đ&acirc;y Qu&yacute; kh&aacute;ch c&oacute; thể tắm biển hay tham gia c&aacute;c tr&ograve; chơi tr&ecirc;n biển như: D&ugrave; k&eacute;o, Lướt v&aacute;n, L&aacute;i Can&ocirc;, Th&aacute;m hiểm dưới đ&aacute;y biển&hellip;(Chi ph&iacute; tự t&uacute;c).</p>\n\n<p>Tham quan xưởng chế t&aacute;c đ&aacute; qu&yacute; (Một trung t&acirc;m trưng b&agrave;y đ&aacute; qu&yacute; đạt ti&ecirc;u chuẩn ISO 9001) Th&aacute;i Lan được biết đến l&agrave; một quốc gia nổi tiếng khai th&aacute;c về đ&aacute; qu&yacute;.</p>\n\n<p>Tham quan To&agrave; nh&agrave; Tỷ Ph&uacute; - một trong những c&ocirc;ng tr&igrave;nh kiến tr&uacute;c ho&agrave;nh tr&aacute;ng v&agrave; tốn k&eacute;m bậc nhất Th&aacute;i Lan.</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><img alt="" src="http://dulichkhonggian.vn/ckfinder/userfiles/images/Image_Tour_Nuoc_Ngoai/20130708060749_73827.jpg" style="height:268px; width:432px" /></p>\n\n<p>&nbsp;</p>\n\n<p>Qu&yacute; kh&aacute;ch ăn trưa,Tiếp đến tham quan Tr&acirc;n Bảo Phật Sơn - n&uacute;i Phật được khắc bằng v&agrave;ng 24k, cao 140 m&eacute;t được chiếu bằng tia laze khắc l&ecirc;n v&aacute;ch n&uacute;i để tặng cho vua Rama IX nh&acirc;n dịp 50 năm trị v&igrave; vương quốc Th&aacute;i Lan.</p>\n\n<p>Qu&yacute; kh&aacute;ch tiếp tục tham quan Bảo T&agrave;ng S&aacute;p của Th&aacute;i Lan.</p>\n\n<p>&nbsp;</p>\n\n<p><img alt="" src="http://dulichkhonggian.vn/ckfinder/userfiles/images/Image_Tour_Nuoc_Ngoai/du-lich-thai-lan-22.jpg" style="height:305px; width:500px" /></p>\n\n<p>&nbsp;</p>\n\n<p>Sau đ&oacute; xe đưa đo&agrave;n tham quan Vườn nho Silver Lake, một c&aacute;nh đồng nho rộng lớn với rất nhiều&nbsp;</p>\n\n<p>hoa, c&oacute; cả vườn bầu khổng lồ, b&iacute; khổng lồ. Chủ nh&acirc;n của khu vườn n&agrave;y l&agrave; một c&ocirc; g&aacute;i đẹp, khu vườn n&agrave;y l&agrave; m&oacute;n qu&agrave; của một người trong ho&agrave;ng gia tặng cho người y&ecirc;u m&igrave;nh. Ph&iacute;a b&ecirc;n ngo&agrave;i c&oacute; một cửa h&agrave;ng lớn b&aacute;n c&aacute;c sản phẩm l&agrave;m từ nho, nước &eacute;p nho, kẹo nho, mứt nho... Tại đ&acirc;y qu&yacute; kh&aacute;ch c&oacute; thể tự do mua qu&agrave; về biếu tặng bạn b&egrave; v&agrave; gia đ&igrave;nh.</p>\n\n<p>Chiều tối. Sau đ&oacute; qu&yacute; kh&aacute;ch sẽ được tận hưởng dịch vụ massage Th&aacute;i cổ truyền gi&uacute;p lưu th&ocirc;ng kh&iacute; huyết v&agrave; đ&aacute;nh thức sức mạnh tiềm ẩn trong cơ thể. Sau đ&oacute; sẽ l&agrave; thời gian tự do mua sắm tại c&aacute;c si&ecirc;u thị v&agrave; thưởng thức c&aacute;c m&oacute;n ăn Th&aacute;i Lan. Qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối v&agrave; tự do kh&aacute;m ph&aacute; Pataya&hellip;&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><img alt="" src="http://dulichkhonggian.vn/ckfinder/userfiles/images/Image_Tour_Nuoc_Ngoai/du-lich-cho-noi-thai-lan_1407482815.jpg" style="height:293px; width:450px" /></p>\n\n<p>&nbsp;</p>\n\n<p><strong>NG&Agrave;Y 04: PATTAYA &ndash; BANGKOK (Ăn s&aacute;ng, trưa, chiều)</strong></p>\n\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn. Xe đưa Qu&yacute; kh&aacute;ch tham quan + Sau khi ăn s&aacute;ng, Đo&agrave;n sẽ tham quan<strong>Vườn th&uacute; hoang d&atilde; SAFARI WORLD KHAO KHEOW&nbsp;</strong>&nbsp;- Tại đ&acirc;y qu&yacute; kh&aacute;ch sẽ được tận mắt chứng kiến đời sống hoang d&atilde; của những lo&agrave;i th&uacute; qu&yacute; hiếm tr&ecirc;n Thế Giới, tận hưởng c&aacute;c chương tr&igrave;nh show biểu diễn ho&agrave;nh tr&aacute;ng</p>\n\n<p><strong>Tham quan Trung t&acirc;m yến s&agrave;o&nbsp;</strong>v&agrave; t&igrave;m hiểu về c&aacute;c loại yến.</p>\n\n<p>Đến&nbsp;<strong>cửa h&agrave;ng b&aacute;n c&aacute;c loại thổ sản Th&aacute;i&nbsp;</strong>để d&ugrave;ng thử v&agrave; mua sắm.</p>\n\n<p><img alt="" src="http://dulichkhonggian.vn/ckfinder/userfiles/images/Image_Tour_Nuoc_Ngoai/vacation-saithip-butterfly-garden-pattaya.jpg" style="height:336px; width:500px" /></p>\n\n<p>&nbsp;</p>\n\n<p><strong><em>Qu&yacute; kh&aacute;ch được thưởng thức BUFFET cao cấp với hơn 150 m&oacute;n tự chọn: Tổ Yến, Thịt B&ograve; ngoại nhập&hellip;tại T&ograve;a Nh&agrave; 88 tầng Baiyoke Skye</em></strong>&nbsp;&ndash; cao nhất thủ đ&ocirc; Bangkok. Tại đ&acirc;y qu&yacute; kh&aacute;ch c&ograve;n đươc chi&ecirc;m ngưỡng to&agrave;n cảnh thủ đ&ocirc; Bangkok v&agrave; lưu lại những khoảnh khắc tuyện vời tại đ&acirc;y.</p>\n\n<p>Tham quan&nbsp;<strong>ch&ugrave;a Phật V&agrave;ng lớn nhất thế giới</strong>: cao 3 m&eacute;t v&agrave; nặng hơn 5,5 tấn. Tượng được đ&uacute;c theo phong c&aacute;ch Sukhothai tĩnh lặng v&agrave; được kh&aacute;m ph&aacute; một c&aacute;ch t&igrave;nh cờ v&agrave;o thập ni&ecirc;n 1950 Người địa phương cho rằng bức tượng lớn nhất thế giới n&agrave;y biểu thị cho sự thịnh vượng v&agrave; thuần khiết cũng như sức mạnh v&agrave; quyền năng.</p>\n\n<p>&nbsp;</p>\n\n<p><img alt="" src="http://dulichkhonggian.vn/ckfinder/userfiles/images/Image_Tour_Nuoc_Ngoai/chuaphatvang.jpg" style="height:335px; width:500px" /></p>\n\n<p>&nbsp;</p>\n\n<p>Tham quan&nbsp;<strong>Trung t&acirc;m nghi&ecirc;n cứu rắn độc Ho&agrave;ng gia.</strong></p>\n\n<p>Đo&agrave;n đến tham quan&nbsp;<strong>ng&ocirc;i ch&ugrave;a thuyền Wat Yannawa</strong>&nbsp;nổi tiếng linh thi&ecirc;ng nhất Bangkok</p>\n\n<p>Sau đ&oacute; Qu&yacute; kh&aacute;ch sẽ thưởng thức&nbsp;<strong>chương tr&igrave;nh biểu diễn ca m&uacute;a nhạc ho&agrave;nh tr&aacute;ng (show B&ecirc;đ&ecirc;) do c&aacute;c vũ c&ocirc;ng chuyển đổi giới t&iacute;nh thực hiện</strong></p>\n\n<p>&nbsp;</p>\n\n<p><strong>NG&Agrave;Y 05: THAM QUAN BANGKOK (free day)</strong></p>\n\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn. Qu&yacute; kh&aacute;ch tự do đi tham quan mua sắm tại c&aacute;c si&ecirc;u thị lớn như: WORLD TRADE CENTRE, BAIYOKE SKY, PRATUNAM MARKET, PLATINUM, ROBINSON SILOM, RACHADA SAGO MARKET</p>\n\n<p>Qu&yacute; kh&aacute;ch c&oacute; thể đăng k&yacute; tham quan c&aacute;c địa danh nổi tiếng kh&aacute;c của Th&aacute;i Lan như:&nbsp;<strong>Đền cổ Ayutthaya, Mini Siam, Siam Miramit,&hellip;.( ch&iacute; ph&iacute; tự t&uacute;c )</strong></p>\n\n<p><img alt="" src="http://dulichkhonggian.vn/ckfinder/userfiles/images/Image_Tour_Nuoc_Ngoai/du-lich-chao-phraya.jpg" style="height:333px; width:500px" /></p>\n\n<p>&nbsp;</p>\n\n<p><strong>NG&Agrave;Y 06: &nbsp;BANGKOK - TP. HỒ CH&Iacute; MINH (Ăn s&aacute;ng)</strong></p>\n\n<p>Ăn s&aacute;ng tại kh&aacute;ch sạn. Đo&agrave;n khởi h&agrave;nh tham quan Trung T&acirc;m trưng b&agrave;y c&aacute;c loại thuộc da nổi tiếng của Th&aacute;i Lan như da c&aacute; sấu, c&aacute; đuối, voi ... Sau đ&oacute; xe đưa Qu&yacute; kh&aacute;ch ra s&acirc;n bay Survanahumi đ&aacute;p chuyến bay về TP.HCM. Đo&agrave;n về đến s&acirc;n bay T&acirc;n Sơn Nhất. Kết th&uacute;c chuyến tham quan. Chia tay đo&agrave;n v&agrave; hẹn gặp lại.</p>', 32, 1);

